import { useNavigate } from 'react-router-dom';
import Icon from '@mdi/react';
import { mdiPlus, mdiCheckBold } from '@mdi/js';

import { ListRow } from 'src/ui/common/element/muListRow';
import { PHButton } from "../../common/element/muButton";
import { Divider } from 'src/ui/common/element/divider';
import './playlistList.css';


const PlayCoPlaylistList = (props) => {
  let navigate = useNavigate();

  return <section className='playCoListMain' {...props}>
    <header>
      <h2>PlayCO</h2>
    </header>
    <aside className='playCoListAside'>
      <div>
        <input
          title='ID of playlist to enter'
          placeholder='참여할 플레이리스트 ID'
          className='playCoIdToEnterTextField'></input>
        <PHButton variant='success' width='100%'>
          <Icon path={mdiCheckBold} size='1.1rem' />&nbsp;&nbsp;재생목록에 참여하기
        </PHButton>
        <Divider
          style={{
            marginLeft: 0,
            marginRight: 0,
            marginTop: '1rem',
            marginBottom: '1rem',
            width: '100%',
          }} />

        <header>
          <h5>내 재생목록</h5>
        </header>
        <div className='playCoAsideSectionHeaderButtonContainer'>
          <PHButton smaller variant='outline-info'>
            <Icon path={mdiPlus} size='1.1rem' />&nbsp;&nbsp;새 재생목록 만들기
          </PHButton>
        </div>
        {
          props.playlistList.map((value, index, array) => {
            return <>
              <ListRow
                className='playCoListItem'
                style={{
                  border: 'none',
                  borderRadius: 'none',
                }}
                onClick={() => navigate('/playco/detail')}
              >
                <div className='playCoListItemTextContainer'>
                  <h6 className='playCoListTitleContainer'>{value.name}</h6>
                  <div className='playCoListAdditionalInfoContainer'>
                    <div>{
                      (value.items.length) ? `${value.items.length}개` : '비어있는 재생목록'
                    }</div>
                    <div>{
                      (value.listener.length)
                        ? `${value.listener.length}명이 참여 중이에요!` : ''
                    }</div>
                  </div>
                </div>
              </ListRow>
              {
                (index === (array.length - 1))
                  ? <></>
                  : <Divider />
              }
            </>
          })
        }
      </div>
    </aside>
  </section>;
};

export { PlayCoPlaylistList };
