import { io, Manager } from "socket.io-client";

let playCoSocketIOHandlerInstance: PlayCoSocketIOHandler;
export class PlayCoSocketIOHandler {
    socketIoMgr: io.Manager
    socketIo: io

    socketIoEventHandler: Record<string, () => any> = {
        onConnect: () => {
            //
        },
        onDisconnect: () => {
            //
        },
        onPlaylistUpdate: () => {
            //
        }
    }
    socketIoExecuteFunc: Record = {

        connectToPlayCo: () => {
            //
            const reqData = {
                eventName: 'onPlaycoConnect',
                data: {
                    access_token: '',
                    csrf_token: '',
                },
            };
        },

        enterPlaylist: (playlistId: number) => {
            const reqData = {
                eventName: 'onPlaylistEnter',
                data: {
                    playlist_id: 0,

                },
            };
        },
        leavePlaylist: (playlistId: number) => {
            //
        },

        setPlaylistStatus: () => {
            const reqData = {
                eventName: 'onPlaylistSetStatus',
                data: {
                    playlist_id: 0,
                    status: {}
                }
            }
        },

    }

    constructor() {
        if (playCoSocketIOHandlerInstance) return playCoSocketIOHandlerInstance;

        this.socketIoMgr = new Manager('https://hyperion.mudev.cc', {
            reconnectionDelayMax: 10000,
            path: '/api/dev/ws',
        });
        this.socketIo = this.socketIoMgr.socket('/playco_ws', {});

        // Register some event handler


        playCoSocketIOHandlerInstance = this;
    }

}
