import React from 'react';

import Button from 'react-bootstrap/Button';
import Spinner from 'react-bootstrap/Spinner';

import { PHButton } from 'src/ui/common/element/muButton';

export const CustomSpinnerButton = (inProps) => {
  // TODO: MAYBE WE SHOULD NOT COPY PROPS LIKE THIS.
  let props = Object.assign({}, inProps, {});

  // This disables button and shows spinner when props.showSpinner is true
  let shouldSpinnerShow = props.showSpinner || false;

  let btnChildren = props.children || '\u00A0';
  if (React.Children.count(btnChildren) > 1)
    throw Error('CustomSpinnerButton accepts only one element for the children.');

  if (shouldSpinnerShow) {
    props.disabled = true;
  }
  btnChildren;


  return <PHButton {...props}>
    {btnChildren}
  </PHButton>
}
