import React, { useState } from 'react';
import { Modal } from "react-bootstrap";
import Form from 'react-bootstrap/Form';
import { PHFormText } from 'src/ui/common/element/muFormText';
import { PHSpinnerButton } from 'src/ui/common/element/muButton';

interface PlaylistCreateModalPropTypes {
  modalShowState: boolean;
  setModalShowState: (boolean) => void;
}

export const PlaylistCreateModal = (props: PlaylistCreateModalPropTypes) => {
  const [playlistCreateModalState, setPlaylistCreateModalState] = useState({
    isProcessing: false,

    nameTextField: '',
  });

  const onPlaylistCreateFunc = (evt) => { console.log(evt); };
  const closeModalFunc = () => props.setModalShowState(false);

  return <Modal
    show={props.modalShowState}
    onHide={closeModalFunc}
    backdrop='static'
    keyboard={false}
    aria-labelledby='contained-modal-title-vcenter'
    centered >
    <Modal.Header closeButton>
      <Modal.Title><h2>재생목록 만들기</h2></Modal.Title>
    </Modal.Header>
    <Modal.Body>
      <Form>
        <Form.Group>
          <Form.Label>아이디 또는 이메일</Form.Label>
          <Form.Control
            type='text'
            placeholder='ID / Email'
            disabled={playlistCreateModalState.isProcessing}
            // value={playlistCreateModalState.idTextFieldValue}
            // onChange={
            //   (event) => setSignInFormData({
            //     ...signInFormData,
            //     idTextFieldValue: event.target.value,
            //   })}
          />
          <PHFormText>{playlistCreateModalState.idNotUsableReason}</PHFormText>
        </Form.Group>

        <PHFormText>{playlistCreateModalState.signInFailedReason}</PHFormText>
      </Form>

    </Modal.Body>
    <Modal.Footer>
      <PHSpinnerButton
        variant='secondary'
        size={false}
        style={{ margin: undefined }}
        onClick={closeModalFunc}
        showSpinner={playlistCreateModalState.isProcessing}>
        취소
      </PHSpinnerButton>

      <PHSpinnerButton
        variant='primary'
        size={false}
        style={{ margin: undefined }}
        onClick={onPlaylistCreateFunc}
        showSpinner={playlistCreateModalState.isProcessing}>
        재생목록 만들기
      </PHSpinnerButton>
    </Modal.Footer>
  </Modal>;
}
