const footerStyle = {
  textAlign: 'center',
  padding: '2rem 1rem',
  scrollSnapAlign: 'end',
  color: 'rgba(255, 255, 255, 0.8)'
};

const HomeFooterSection = props => {
  const generateEmailAddress = (event) => {
    let dataset = event.target.dataset;
    event.target.href = 'mailto:' + dataset.name + '@' + dataset.domain + '.' + dataset.tld;
  };

  return <footer style={footerStyle} {...props}>
    MUsoftware (&nbsp;
    <a href="#"
      data-name="musoftware"
      data-domain="mudev"
      data-tld="cc"
      onClick={(e) => generateEmailAddress(e)} >
      Mail
    </a> )<br />
  </footer>;
};

export { HomeFooterSection };
