import FrostAPI from "../../network/api";

export class AccountInfo {
  uuid: number;
  id: string;
  nick: string;
  email: string;

  constructor(inObj: any) {
    this.uuid = inObj.uuid;
    this.id = inObj.id;
    this.nick = inObj.nick;
    this.email = inObj.email;
  }
}

export const SET_ACCOUNT_INFO_FORCE = 'account/SET_ACCOUNT_INFO_FORCE' as const;
export const SIGNIN = 'account/SIGNIN' as const;
export const SIGNUP = 'account/SIGNUP' as const;
export const SIGNOUT = 'account/SIGNOUT' as const;
export const REFRESH = 'account/REFRESH' as const;

export const calculateInitialAccountState = () => {
  return {
    accountInfo: new AccountInfo({ uuid: 0, id: '@musoftware', nick: 'MU', email: 'musoftware@daum.net' })
  }
}

export const setAccountInfoForce = (accountInfo: AccountInfo) => {
  return {
    actionType: SET_ACCOUNT_INFO_FORCE,
    accountInfo: accountInfo,
  }
}

export const signIn = () => {

}
export const signUp = () => {

}
export const signOut = () => {

}
export const refresh = () => {

}


export default function accountReducer(
  state: AccountInfo = calculateInitialAccountState(),
  action: AccountInfoAction) {

    switch (action.actionType) {
    case SET_ACCOUNT_INFO_FORCE: {
      return {
        ...state,
        accountInfo: action.accountInfo,
      }
    }
    default:
      return state;
  }
};
