import React, { Component, useState } from 'react';
import { useNavigate } from 'react-router-dom';

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import { isEmail } from 'validator';

import { PHSpinnerButton } from 'src/ui/common/element/muButton';
import { CustomFormText } from './element/CustomFormText';
import './account.css';

const calculateIncludedCharType = (str: string) => {
  const strType: string[] = [];

  for (var i = 0; i < str.length; i++) {
    let currentChar = str.charAt(i);
    if (currentChar >= '0' && currentChar <= '9') {
      strType.push('n'); // number
    } else if (currentChar >= 'a' && currentChar <= 'z') {
      strType.push('l'); // lower
    } else if (currentChar >= 'A' && currentChar <= 'Z') {
      strType.push('u'); // upper
    } else if (currentChar >= ' ' && currentChar <= '~') {
      strType.push('s'); // special char
    } else {
      strType.push('e'); // error or unknown
    }
  }

  return new Set(strType);
}

const AccountSignUp = () => {
  const navigate = useNavigate();
  const [signUpFormData, setSignUpFormData] = useState({
    idTextFieldValue: '',
    pwTextFieldValue: '',
    pwReTypeTextFieldValue: '',
    emailTextFieldValue: '',
    nickTextFieldValue: '',

    idNotUsableReason: null,
    nickNotUsableReason: null,
    emailNotUsableReason: null,
    pwNotUsableReason: null,
    pwReTypeNotUsableReasonleReason: null,
    signUpFailedReason: null,
    isProcessing: false,
  });

  const InputChecker = {
    id: (str: string) => {
      if (!str) {
        setSignUpFormData({
          ...signUpFormData,
          idNotUsableReason: '아이디를 입력해주세요.',
        });
        return false;
      } else if (str.length < 4) {
        setSignUpFormData({
          ...signUpFormData,
          idNotUsableReason: '아이디의 길이가 너무 짧아요, 4자 이상으로 적어주세요.',
        });
        return false;
      } else if (48 < str.length) {
        setSignUpFormData({
          ...signUpFormData,
          idNotUsableReason: '아이디의 길이가 너무 길어요, 48자 이하로 적어주세요.',
        });
        return false;
      }

      const strType = calculateIncludedCharType(str);
      if (strType.has('e')) {
        setSignUpFormData({
          ...signUpFormData,
          idNotUsableReason: '아이디로 사용할 수 없는 글자가 포함되어 있어요, 다시 입력해주세요.',
        });
        return false;
      }

      setSignUpFormData({
        ...signUpFormData,
        idNotUsableReason: null,
      });
      return true;
    },
    email: (str: string) => {
      if (!str) {
        setSignUpFormData({
          ...signUpFormData,
          emailNotUsableReason: '이메일을 입력해주세요.',
        });
        return false;
      }
      if (!isEmail(str)) {
        setSignUpFormData({
          ...signUpFormData,
          emailNotUsableReason: '올바르지 않은 이메일 형식이에요.',
        });
        return false;
      }
      setSignUpFormData({
        ...signUpFormData,
        emailNotUsableReason: null,
      });
      return true;
    },
    nick: (str: string) => {
      if (!str) {
        setSignUpFormData({
          ...signUpFormData,
          nickNotUsableReason: '별칭을 입력해주세요.',
        });
        return false;
      }

      setSignUpFormData({
        ...signUpFormData,
        nickNotUsableReason: null,
      });
      return true;
    },
    pw: (str: string) => {
      if (!str) {
        setSignUpFormData({
          ...signUpFormData,
          pwNotUsableReason: '사용하실 비밀번호를 입력해주세요,\n',
        });
        return false;
      } else if (str.length < 8) {
        setSignUpFormData({
          ...signUpFormData,
          pwNotUsableReason: '길이가 너무 짧아요,\n',
        });
        return false;
      } else if (1024 < str.length) {
        setSignUpFormData({
          ...signUpFormData,
          pwNotUsableReason: '길이가 너어어어무 길어요,\n',
        });
        return false;
      }

      const strType = calculateIncludedCharType(str);
      if (strType.has('e')) {
        setSignUpFormData({
          ...signUpFormData,
          pwNotUsableReason: '비밀번호로 사용할 수 없는 글자가 포함되어 있어요,\n',
        });
        return false;
      } else if (strType.length < 2) {
        setSignUpFormData({
          ...signUpFormData,
          pwNotUsableReason: '비밀번호가 너무 단순해요,\n'
        });
        return false;
      }

      setSignUpFormData({
        ...signUpFormData,
        pwNotUsableReason: null,
      });
      return true;
    },
    pwReType: (pwStr: string, pwReTypeStr: string) => {
      if (!pwStr) {
        setSignUpFormData({
          ...signUpFormData,
          pwReTypeNotUsableReason: '위에 입력하신 비밀번호를 한번 더 입력해주세요.',
        });
        return false;
      } else if (pwStr === pwReTypeStr) {
        setSignUpFormData({
          ...signUpFormData,
          pwReTypeNotUsableReason: null,
        });
        return true;
      }

      setSignUpFormData({
        ...signUpFormData,
        pwReTypeNotUsableReason: '위에 입력하신 비밀번호와 일치하지 않아요.',
      });
      return false;
    },
  }

  const handleInput = (fieldName: string, fieldValue: string) => {
    const fieldStateKey = `${fieldName}TextFieldValue`;
    console.log(newState);
    setSignUpFormData({
      ...signUpFormData,
      fieldStateKey: fieldValue,
    });

    InputChecker[fieldName]();
  };
  const trySignUp = () => {
    setSignUpFormData({
      ...signUpFormData,
      isProcessing: true,
    });

    console.log(signUpFormData.idTextFieldValue);
    console.log(signUpFormData.emailTextFieldValue);
    console.log(signUpFormData.nickTextFieldValue);
    console.log(signUpFormData.pwTextFieldValue);
    console.log(signUpFormData.pwReTypeTextFieldValue);
    // dispatch(signInActionCreatorAsync());

    // Run below if success
    // navigate(-1);
    setTimeout(
      () => setSignUpFormData({
        ...signUpFormData,
        isProcessing: false,
      }), 5000
    );
  };

  return <section className='accountMain'>
    <header>
      <h2>계정 만들기</h2>
    </header>
    <aside className='accountAside'>
      <Form>
        <Form.Group className='accountAsideFormGroup' controlId='formSignUpId'>
          <Form.Label>아이디</Form.Label>
          <Form.Control
            type='text'
            placeholder='아이디를 입력해주세요'
            disabled={signUpFormData.isProcessing}
            value={signUpFormData.idTextFieldValue}
            onChange={(event) => handleInput('id', event.target.value)}
          />
          <CustomFormText>{signUpFormData.idNotUsableReason}</CustomFormText>
        </Form.Group>

        <Form.Group className='accountAsideFormGroup' controlId='formSignUpNick'>
          <Form.Label>별칭</Form.Label>
          <Form.Control
            type='text'
            placeholder='닉네임을 입력해주세요'
            disabled={signUpFormData.isProcessing}
            value={signUpFormData.nickTextFieldValue}
            onChange={(event) => handleInput('nick', event.target.value)}
          />
          <CustomFormText>{signUpFormData.nickNotUsableReason}</CustomFormText>
        </Form.Group>

        <Form.Group className='accountAsideFormGroup' controlId='formSignUpEmail'>
          <Form.Label>이메일</Form.Label>
          <Form.Control
            type='email'
            placeholder='someone@example.com'
            disabled={signUpFormData.isProcessing}
            value={signUpFormData.emailTextFieldValue}
            onChange={(event) => handleInput('email', event.target.value)}
          />
          <CustomFormText>{signUpFormData.emailNotUsableReason}</CustomFormText>
        </Form.Group>

        <Form.Group className='accountAsideFormGroup' controlId='formSignUpPassword'>
          <Form.Label>비밀번호</Form.Label>
          <Form.Control
            type='password'
            placeholder='비밀번호'

            disabled={signUpFormData.isProcessing}
            value={signUpFormData.pwTextFieldValue}
            onChange={(event) => handleInput('pw', event.target.value)}
          />
          <CustomFormText defaultChildren='비밀번호는 8자 이상, 대소문자/숫자/특수문자 중 2가지 이상의 조합으로 입력해주세요.'>
            {signUpFormData.pwNotUsableReason ? signUpFormData.pwNotUsableReason + '비밀번호는 8자 이상, 대소문자/숫자/특수문자 중 2가지 이상의 조합으로 입력해주세요.' : ''}
          </CustomFormText>
        </Form.Group>

        <Form.Group className='accountAsideFormGroup' controlId='formSignUpPasswordReType'>
          <Form.Label>비밀번호 확인</Form.Label>
          <Form.Control
            type='password'
            placeholder='위에 입력하신 비밀번호를 다시 입력해주세요'
            disabled={signUpFormData.isProcessing}
            value={signUpFormData.pwReTypeTextFieldValue}
            onChange={(event) => handleInput('pwReType', event.target.value)}
          />
          <CustomFormText>{signUpFormData.pwReTypeNotUsableReasonleReason}</CustomFormText>
        </Form.Group>

        <CustomFormText className='accountAsideFormGroup'>{signUpFormData.signUpFailedReason}</CustomFormText>

        <div className='accountSubmitBtnContainer'>
          <PHSpinnerButton
            variant='primary'
            type='submit'
            size={false}
            style={{ margin: undefined }}
            onClick={() => trySignUp()}
            showSpinner={signUpFormData.isProcessing}>
            계정 만들기
          </PHSpinnerButton>

          <Button
            variant='outline-light'
            disabled={signUpFormData.isProcessing}
            onClick={() => navigate('/account/signin')}>
            로그인하러 가기
          </Button>
        </div>
      </Form>
    </aside>
  </section>;
}

export { AccountSignUp };
