import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import { CustomFormText } from './element/CustomFormText';
import { CustomSpinnerButton } from './element/CustomSpinnerButton';
import './account.css';

const AccountSignIn = () => {
  let navigate = useNavigate();
  const [signInFormData, setSignInFormData] = useState({
    idNotUsableReason: null,
    passwordNotUsableReason: null,
    signInFailedReason: null,
    isProcessing: false,
  });

  return <section className='accountMain'>
    <header>
      <h2>로그인</h2>
    </header>
    <aside className='accountAside'>
      <Form>
        <Form.Group className='accountAsideFormGroup' controlId='formBasicEmail'>
          <Form.Label>아이디 또는 이메일</Form.Label>
          <Form.Control type='text' placeholder='ID / Email' />
          <CustomFormText>{signInFormData.idNotUsableReason}</CustomFormText>
        </Form.Group>

        <Form.Group className='accountAsideFormGroup' controlId='formBasicPassword'>
          <Form.Label>비밀번호</Form.Label>
          <Form.Control type='password' placeholder='비밀번호' />
          <CustomFormText>{signInFormData.passwordNotUsableReason}</CustomFormText>
        </Form.Group>

        <CustomFormText className='accountAsideFormGroup'>{signInFormData.signInFailedReason}</CustomFormText>

        <div className='accountSubmitBtnContainer'>
          <Button
            variant='primary'
            type='submit'>로그인</Button>
          <Button
            variant='outline-light'
            onClick={() => navigate('/account/signup')}>
            계정 만들기
          </Button>
        </div><br />
        <a className='signInGoToResetPassword' href='#' onClick={() => navigate('/account/reset-password')}>
          비밀번호를 잊어버리셨나요?
        </a>
        <CustomSpinnerButton
          variant='primary'
          onClick={() => {
            setSignInFormData({
              ...signInFormData,
              isProcessing: true,
            });
            setTimeout(() => {
              setSignInFormData({
                ...signInFormData,
                isProcessing: false,
              });
            }, 5000);
          }}
          showSpinner={signInFormData.isProcessing}
          setGrayWhenDisabled>
          <div>
            qweqweqweqweqwe<br />
          </div>
        </CustomSpinnerButton>
      </Form>
    </aside>
  </section>;
}

export { AccountSignIn };
