import './main.css';

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { PHButton } from '@src/ui/common/element/muButton';
import { Divider } from '@src/ui/common/element/divider';
import FrostAPI from 'src/network/api';
import { FrostError } from 'src/common/error';
import { Loader } from 'react-loader-spinner';

class PlaycoMain extends React.Component {
  frostAPI = new FrostAPI();

  constructor(props) {
    super(props);

    this.state = {
      isSignedIn: null, // Three state boolean
    };
  }

  componentDidMount() {
    frostAPI.isSignedIn(true).then((signedInResult) => this.setState({
      isSignedIn: signedInResult
    }));
  }

  render() {
    const { isSignedIn } = this.state;

    let playCoBody = isSignedIn ? <>
      로그인 됨
    </> : isSignedIn === false ? <aside>
      이 기능은 로그인이 필요해요.<br />
      <PHButton onClick={() => alert(-1)} variant='success' size='medium'>로그인</PHButton>
    </aside> : <Loader />;


    return <section className='playCoMain' {...props}>
      <header>
        <h2>PlayCO</h2>
        <p>플레이리스트를, 멀리서, 그러나 같이</p>
      </header>
      <Divider style={{ width: '70%', }} />
      {playCoBody}
    </section>
  }
};

export { PlaycoMain };
