import React, { useState } from 'react';
import { Modal, Button } from "react-bootstrap";
import Form from 'react-bootstrap/Form';
import { PHFormText } from 'src/ui/common/element/muFormText';
import { PHSpinnerButton } from 'src/ui/common/element/muButton';
// import {
//   ListRowLongTextType,
//   L,
//   ListRowInputType,
//   ListRowButtonType,
//   ListRow
// } from 'src/ui/common/element/muListRow';

interface PlaylistCreateModalPropTypes {
  modalShowState: boolean;
  setModalShowState: (boolean) => void;
}

export const PlaylistCreateModal = (props: PlaylistCreateModalPropTypes) => {
  const [playlistCreateModalState, setPlaylistCreateModalState] = useState({
    isProcessing: false,

    nameTextFieldValue: '',
    nameNotUsableReason: 'asd',
    isPublicAccessable: false,
    isPublicItemAppendable: false,
    isPublicItemDeletable: false,

    playlistCreationFailedReason: '',
  });
  const closeModalFunc = () => props.setModalShowState(false);
  const handleInputChange = (valueName) => (e) => {
    setPlaylistCreateModalState({
      ...playlistCreateModalState,
      [valueName]: (e.target.type === 'checkbox') ? e.target.checked : e.target.value,
    });
  }
  const tryPlaylistCreate = () => {
    console.log(playlistCreateModalState);
    setPlaylistCreateModalState({
      ...playlistCreateModalState,
      isProcessing: true,
    });

    setTimeout(() => {
      setPlaylistCreateModalState({
        ...playlistCreateModalState,
        isProcessing: false,
      });
    }, 5000);
  }

  return <Modal
    show={props.modalShowState}
    onHide={closeModalFunc}
    backdrop='static'
    keyboard={false}
    aria-labelledby='contained-modal-title-vcenter'
    centered >
    <Modal.Header closeButton>
      <Modal.Title><h5 style={{ margin: 0, }}>재생목록 만들기</h5></Modal.Title>
    </Modal.Header>
    <Modal.Body>
      <Form>
        <Form.Group>
          <Form.Label>새 재생목록의 이름</Form.Label>
          <Form.Control
            type='text'
            placeholder='새로 만드실 재생목록의 이름을 적어주세요'
            disabled={playlistCreateModalState.isProcessing}
            value={playlistCreateModalState.nameTextFieldValue}
            onChange={
              (event) => setPlaylistCreateModalState({
                ...playlistCreateModalState,
                nameTextFieldValue: event.target.value,
              })} />
          <PHFormText>{playlistCreateModalState.nameNotUsableReason}</PHFormText>
        </Form.Group>
        <Form.Group>
          <Form.Label>재생목록 설정</Form.Label>
          <Form.Switch
            onChange={handleInputChange('isPublicAccessable')}
            value={playlistCreateModalState.isPublicAccessable}
            disabled={playlistCreateModalState.isProcessing}
            id='isPublicAccessableSwitch'
            label='다른 사람이 재생목록을 볼 수 있어요.' />
          <Form.Switch
            onChange={handleInputChange('isPublicItemAppendable')}
            value={playlistCreateModalState.isPublicItemAppendable}
            disabled={playlistCreateModalState.isProcessing}
            id='isPublicItemAppendableSwitch'
            label='다른 사람이 재생목록에 영상을 추가할 수 있어요.' />
          <Form.Switch
            onChange={handleInputChange('isPublicItemDeletable')}
            value={playlistCreateModalState.isPublicItemDeletable}
            disabled={playlistCreateModalState.isProcessing}
            id='isPublicItemDeletableSwitch'
            label='다른 사람이 재생목록에서 영상을 삭제할 수 있어요.' />
        </Form.Group>
        <PHFormText>{playlistCreateModalState.playlistCreationFailedReason}</PHFormText>
      </Form>
    </Modal.Body>
    <Modal.Footer>
      <Button
        variant='secondary'
        onClick={closeModalFunc}
        disabled={playlistCreateModalState.isProcessing}>
        취소
      </Button>

      <PHSpinnerButton
        variant='primary'
        size={false}
        style={{ margin: undefined }}
        onClick={tryPlaylistCreate}
        showSpinner={playlistCreateModalState.isProcessing}>
        재생목록 만들기
      </PHSpinnerButton>
    </Modal.Footer>
  </Modal>;
}
