import {
  ACCOUNT_FETCHING,
  SIGNED_IN,
  SIGNED_OUT,
} from './action';
import {
  signInActionCreatorAsync,
  signUpActionCreatorAsync,
  signOutActionCreatorAsync,
  refreshAccessTokenActionCreatorAsync,
  setAccountInfoForceActionCreator, // TODO: DEBUG PURPOSE, REMOVE THIS ON RELEASE!
} from './action_creator';
import { AccountInfo } from './model';

export default function accountReducer(state: AccountInfo = null, action: string) {
  switch (action) {
    case SET_ACCOUNT_INFO_FORCE:
      return {
        ...state,
        accountInfo: state,
      }

    default:
      break;
  }
  return state;
}
