import './App.css';
import PropTypes from 'prop-types';
import { Routes, Route, BrowserRouter } from 'react-router-dom';

import { initializeSidebar } from './ui/util/sidebar';
import { SIDEBAR_ITEMS } from './redux/modules/route';
import { GlowHome } from '@ui/page/home/home';

class LocationListener extends Component {
  static contextTypes = {
    router: PropTypes.object
  };

  componentDidMount() {
    this.handleLocationChange(this.context.router.history.location);
    this.unlisten =
this.context.router.history.listen(this.handleLocationChange);
  }

  componentWillUnmount() {
    this.unlisten();
  }

  handleLocationChange(location) {
    // your staff here
    console.log(`- - - location: '${location.pathname}'`);
  }

  render() {
    return this.props.children;
  }
}

export class App extends Component {
  componentDidMount() {
    initializeSidebar(SIDEBAR_ITEMS);
  }

  render() {
    const onUpdateHook = (qwe) => console.log(qwe)
    return <div className="App">
      <BrowserRouter>
        <LocationListener>
          <Routes onChange={onUpdateHook}>
            <Route exact path="/" className="nav-link" element={<GlowHome />} exact />
            <Route exact path="/projects" className="nav-link" element={<GlowHome />} exact />
            <Route exact path="/containers" className="nav-link" element={<GlowHome />} exact />
          </Routes>
        </LocationListener>
      </BrowserRouter>
    </div>
  }
}

export default App;
