import React, { Component, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';

import {
  signInActionCreatorAsync,
  setForceAccountInfoActionCreator,
  setForceSignOutActionCreator
} from 'src/redux/modules/account/action_creator';
import { AccountInfo } from 'src/redux/modules/account/model';

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import { PHSpinnerButton } from 'src/ui/common/element/muButton';
import { CustomFormText } from './element/CustomFormText';
import './account.css';

const AccountSignIn = () => {
  const navigate = useNavigate();
  const accountInfo = useSelector(state => state.accountReducer);
  const dispatch = useDispatch();
  const [signInFormData, setSignInFormData] = useState({
    idTextFieldValue: '',
    pwTextFieldValue: '',

    idNotUsableReason: null,
    passwordNotUsableReason: null,
    signInFailedReason: null,
    isProcessing: false,
  });
  const trySignIn = () => {
    setSignInFormData({
      ...signInFormData,
      isProcessing: true,
    });

    if (accountInfo.isSignedIn) {
      dispatch(setForceSignOutActionCreator());
    } else {
      dispatch(setForceAccountInfoActionCreator(
        new AccountInfo({
          isSignedIn: true,
          uuid: 1,
          id: 'musoftware',
          nick: 'MUsoftware',
          email: 'musoftware@daum.net'
        })));
    }
    console.log(signInFormData.idTextFieldValue);
    console.log(signInFormData.pwTextFieldValue);
    // dispatch(signInActionCreatorAsync());


    // Run below if success
    // navigate(-1);
    setTimeout(
      () => setSignInFormData({
        ...signInFormData,
        isProcessing: false,
      }), 5000
    );
  };

  return <section className='accountMain'>
    <header>
      <h2>로그인</h2>
    </header>
    <aside className='accountAside'>
      <Form>
        <Form.Group className='accountAsideFormGroup' controlId='formBasicEmail'>
          <Form.Label>아이디 또는 이메일</Form.Label>
          <Form.Control
            type='text'
            placeholder='ID / Email'
            disabled={signInFormData.isProcessing}
            value={signInFormData.idTextFieldValue}
            onChange={
              (event) => setSignInFormData({
                ...signInFormData,
                idTextFieldValue: event.target.value,
              })}
          />
          <CustomFormText>{signInFormData.idNotUsableReason}</CustomFormText>
        </Form.Group>

        <Form.Group className='accountAsideFormGroup' controlId='formBasicPassword'>
          <Form.Label>비밀번호</Form.Label>
          <Form.Control
            type='password'
            placeholder='비밀번호'
            disabled={signInFormData.isProcessing}
            value={signInFormData.pwTextFieldValue}
            onChange={
              (event) => setSignInFormData({
                ...signInFormData,
                pwTextFieldValue: event.target.value,
              })}
          />
          <CustomFormText>{signInFormData.passwordNotUsableReason}</CustomFormText>
        </Form.Group>

        <CustomFormText className='accountAsideFormGroup'>{signInFormData.signInFailedReason}</CustomFormText>

        <div className='accountSubmitBtnContainer'>
          <PHSpinnerButton
            variant='primary'
            size={false}
            style={{ margin: undefined }}
            onClick={() => trySignIn()}
            showSpinner={signInFormData.isProcessing}>
            로그인
          </PHSpinnerButton>

          <Button
            variant='outline-light'
            disabled={signInFormData.isProcessing}
            onClick={() => navigate('/account/signup')}>
            계정 만들기
          </Button>
        </div><br />
        <a
          className='signInGoToResetPassword'
          href='#'
          onClick={() => {
            if (!signInFormData.isProcessing) navigate('/account/reset-password')
          }}>
          비밀번호를 잊어버리셨나요?
        </a>
      </Form>
    </aside>
  </section>;
}

export { AccountSignIn };
