import React, { useState } from 'react';
import ReactPlayer from 'react-player';
import Form from 'react-bootstrap/Form'

import { PlaylistListener, PlaylistItem, Playlist } from './playco/model';
import { PlaylistDetail } from './playlistResource';
import './playlistDetailPage.css';

export const PlayCoPlaylistDetail = (props) => {
  const [playlistDetailState, setPlaylistDetailState] = useState({
    currentPlayUrl: 'https://www.youtube.com/watch?v=AH1tszKHCH4',
    autoplay: false,
  });

  let dummyPlaylistData = new Playlist({
    uuid: 0,
    index: 0,
    name: '테스트',
    created_by_uuid: 0,
    created_by_nick: '',
    public_accessable: false,
    public_modifiable: false,
    current_playing_item_index: 1,
    items: [
      new PlaylistItem({
        uuid: 0,
        index: 0,
        name: 'q',
        data: {},
        added_by_uuid: 0,
        added_by_nick: '',
        link: 'https://www.youtube.com/watch?v=2Dw7NFm1ZfY',
        link_type: 'youtube',
        link_id: '2Dw7NFm1ZfY',
        current_listener: [
          new PlaylistListener({ uuid: 0, name: '123' }),
          new PlaylistListener({ uuid: 0, name: '123' }),
          new PlaylistListener({ uuid: 0, name: '123' })
        ],
      }),
      new PlaylistItem({
        uuid: 0,
        index: 0,
        name: 'q',
        data: {},
        added_by_uuid: 0,
        added_by_nick: '',
        link: 'https://www.youtube.com/watch?v=2Dw7NFm1ZfY',
        link_type: 'youtube',
        link_id: '2Dw7NFm1ZfY',
        current_listener: [
          new PlaylistListener({ uuid: 0, name: '123' }),
          new PlaylistListener({ uuid: 0, name: '123' }),
          new PlaylistListener({ uuid: 0, name: '123' })
        ],
      }),
    ],
  })

  const playerConfig = {
    youtube: {
      playerVars: {
        autoplay: playlistDetailState.autoplay ? 1 : 0,
        controls: 1,
      }
    }
  }

  return <section className='playCoPlaylistDetailPage' {...props}>
    <header>
      <h2>PlayCO</h2>
    </header>
    <div className='playCoPlaylistDetail'>
      {/* playlist aside */}
      <PlaylistDetail playlistData={dummyPlaylistData} />
      <aside className='playCoPlaylistDetailPlayerAside'>
        {/* player aside */}
        <div className='playCoPlaylistDetailPlayerContainer'>
          <ReactPlayer
            url={playlistDetailState.currentPlayUrl}
            className='playCoPlaylistDetailPlayer'
            width='100%' height='100%'
            config={playerConfig}
          />
        </div>
        <div className='playCoPlaylistDetailPlayerBtnGroup'>
          <Form.Switch
            label='자동재생'
            checked={playlistDetailState.autoplay}
            onChange={() => setPlaylistDetailState({ ...playlistDetailState, autoplay: !playlistDetailState.autoplay })}
          />
        </div>
      </aside>
    </div>
  </section>;
};
