import React from 'react';

import Button from 'react-bootstrap/Button';
import Spinner from 'react-bootstrap/Spinner';

import { PHButton } from 'src/ui/common/element/muButton';

const REACT_ELEMENT_TYPE = (typeof Symbol === 'function' && Symbol.for && Symbol.for('react.element')) || 0xeac7;

export const CustomSpinnerButton = (inProps) => {
  // TODO: MAYBE WE SHOULD NOT COPY PROPS LIKE THIS.
  let props = Object.assign({}, inProps, {});

  // When props.showSpinner is true, we disable button and show spinner
  let shouldSpinnerShow = props.showSpinner || false;

  let btnChildren = props.children || '\u00A0';
  if (React.Children.count(btnChildren) > 1)
    throw Error('CustomSpinnerButton accepts only one element for the children.');

  // If btnChild is not an DOMElement or reactElement, then wrap it with plain div.
  if (!(typeof (btnChildren) == 'object' && (btnChildren.$$typeof === REACT_ELEMENT_TYPE)))
    btnChildren = <div>{btnChildren}</div>;

  console.log(typeof (btnChildren));


  if (shouldSpinnerShow) {
    props.disabled = true;
    if (props.setGrayWhenDisabled) { props.variant = 'secondary'; }

    btnChildren;
  }


  return <PHButton {...props}>
    {btnChildren}
  </PHButton>
}
