import { Card } from 'react-bootstrap';
import { ListRowLongTextType, L, ListRowInputType, ListRowButtonType, ListRow } from '@ui/common/element/muListRow';
import { useState } from 'react';

const HomePostToMUSection = props => {
  const [demoData, setDemoData] = useState({
    demoStr: '',
    demoNumeric: 0,
    demoChecked: true,
    demoRadio: undefined,
  });
  const onInputChange = (valueName) => (e) => {
    if (e.target.type === 'reset') {
      setDemoData({
        demoStr: '',
        demoNumeric: 0,
        demoChecked: true,
        demoRadio: undefined,
      });
    } if (valueName) {
      let newDemoData = { ...demoData };
      // TODO: e.target.checked도 받을 수 있도록
      if (e.target.type === 'checkbox') {
        newDemoData[valueName] = e.target.checked;
      } else {
        newDemoData[valueName] = e.target.value;
      }
      console.log(newDemoData);
      setDemoData(newDemoData);
    }
  }

  return <div {...props}>
    <h2>Contact to MU!</h2>
    <Card>
      <ListRow label='성함'>
        {ListRowInputType.n({ name: 'demoNumeric', type: 'number', onChange: onInputChange, state: demoData, min: 5, max: 15, step: 2, })}
      </ListRow>
      <ListRow label='연락처'>
        {[
          '테스트:number',
          ListRowInputType.n({ name: 'demoNumeric', type: 'number', onChange: onInputChange, state: demoData, min: 5, max: 15, step: 2, }),
        ]}
      </ListRow>
      <ListRow label='문의 유형'>
        {[
          '테스트:number',
          ListRowInputType.n({ name: 'demoNumeric', type: 'number', onChange: onInputChange, state: demoData, min: 5, max: 15, step: 2, }),
        ]}
      </ListRow>
      <ListRow label='상세 내용'>
        {[
          '테스트:number',
          ListRowInputType.n({ name: 'demoNumeric', type: 'number', onChange: onInputChange, state: demoData, min: 5, max: 15, step: 2, }),
        ]}
      </ListRow>
    </Card>
  </div>;
};

export { HomePostToMUSection };
