import React, { Component, useState } from 'react';
import { useNavigate } from 'react-router-dom';

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import { PHSpinnerButton } from 'src/ui/common/element/muButton';
import { CustomFormText } from './element/CustomFormText';
import './account.css';


const AccountSignUp = () => {
  const navigate = useNavigate();
  const [signUpFormData, setSignUpFormData] = useState({
    idTextFieldValue: '',
    pwTextFieldValue: '',
    pwReTypeTextFieldValue: '',
    emailTextFieldValue: '',
    nickTextFieldValue: '',

    idNotUsableReason: null,
    nickNotUsableReason: null,
    emailNotUsableReason: null,
    passwordNotUsableReason: null,
    passwordReTypeNotUsableReason: null,
    signUpFailedReason: null,
    isProcessing: false,
  });
  const trySignUp = () => {
    setSignUpFormData({
      ...signUpFormData,
      isProcessing: true,
    });

    console.log(signUpFormData.idTextFieldValue);
    console.log(signUpFormData.emailTextFieldValue);
    console.log(signUpFormData.nickTextFieldValue);
    console.log(signUpFormData.pwTextFieldValue);
    console.log(signUpFormData.pwReTypeTextFieldValue);
    // dispatch(signInActionCreatorAsync());

    // Run below if success
    // navigate(-1);
    setTimeout(
      () => setSignUpFormData({
        ...signUpFormData,
        isProcessing: false,
      }), 5000
    );
  };

  return <section className='accountMain'>
    <header>
      <h2>계정 만들기</h2>
    </header>
    <aside className='accountAside'>
      <Form>
        <Form.Group className='accountAsideFormGroup' controlId='formSignUpId'>
          <Form.Label>아이디</Form.Label>
          <Form.Control
            type='text'
            placeholder='아이디를 입력해주세요'
            disabled={signUpFormData.isProcessing}
            value={signUpFormData.idTextFieldValue}
            onChange={
              (event) => setSignUpFormData({
                ...signUpFormData,
                idTextFieldValue: event.target.value,
              })}
          />
          <CustomFormText>{signUpFormData.idNotUsableReason}</CustomFormText>
        </Form.Group>

        <Form.Group className='accountAsideFormGroup' controlId='formSignUpNick'>
          <Form.Label>별칭</Form.Label>
          <Form.Control
            type='text'
            placeholder='닉네임을 입력해주세요'
            disabled={signUpFormData.isProcessing}
            value={signUpFormData.nickTextFieldValue}
            onChange={
              (event) => setSignUpFormData({
                ...signUpFormData,
                nickTextFieldValue: event.target.value,
              })}
          />
          <CustomFormText>{signUpFormData.nickNotUsableReason}</CustomFormText>
        </Form.Group>

        <Form.Group className='accountAsideFormGroup' controlId='formSignUpEmail'>
          <Form.Label>이메일</Form.Label>
          <Form.Control
            type='email'
            placeholder='someone@example.com'
            disabled={signUpFormData.isProcessing}
            value={signUpFormData.emailTextFieldValue}
            onChange={
              (event) => setSignUpFormData({
                ...signUpFormData,
                emailTextFieldValue: event.target.value,
              })}
          />
          <CustomFormText>{signUpFormData.emailNotUsableReason}</CustomFormText>
        </Form.Group>

        <Form.Group className='accountAsideFormGroup' controlId='formSignUpPassword'>
          <Form.Label>비밀번호</Form.Label>
          <Form.Control
            type='password'
            placeholder='비밀번호'

            disabled={signUpFormData.isProcessing}
            value={signUpFormData.pwTextFieldValue}
            onChange={
              (event) => setSignUpFormData({
                ...signUpFormData,
                pwTextFieldValue: event.target.value,
              })}
          />
          <CustomFormText defaultChildren='비밀번호는 8자 이상, 대소문자/숫자/특수문자 중 2가지 이상의 조합으로 사용해주세요.'>
            {signUpFormData.passwordNotUsableReason ? '비밀번호는 8자 이상, 대소문자/숫자/특수문자 중 2가지 이상의 조합으로 사용해주세요.' : ''}
          </CustomFormText>
        </Form.Group>

        <Form.Group className='accountAsideFormGroup' controlId='formSignUpPasswordReType'>
          <Form.Label>비밀번호 확인</Form.Label>
          <Form.Control
            type='password'
            placeholder='위에 입력하신 비밀번호를 다시 입력해주세요'
            disabled={signUpFormData.isProcessing}
            value={signUpFormData.pwReTypeTextFieldValue}
            onChange={
              (event) => setSignUpFormData({
                ...signUpFormData,
                pwReTypeTextFieldValue: event.target.value,
              })}
          />
          <CustomFormText>{signUpFormData.passwordReTypeNotUsableReason}</CustomFormText>
        </Form.Group>

        <CustomFormText className='accountAsideFormGroup'>{signUpFormData.signUpFailedReason}</CustomFormText>

        <div className='accountSubmitBtnContainer'>
          <PHSpinnerButton
            variant='primary'
            type='submit'
            size={false}
            style={{ margin: undefined }}
            onClick={() => trySignUp()}
            showSpinner={signUpFormData.isProcessing}>
            계정 만들기
          </PHSpinnerButton>

          <Button
            variant='outline-light'
            disabled={signUpFormData.isProcessing}
            onClick={() => navigate('/account/signin')}>
            로그인하러 가기
          </Button>
        </div>
      </Form>
    </aside>
  </section>;
}

export { AccountSignUp };
