import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { parse_host } from 'tld-extract';

import { OverlayTrigger, Tooltip, Form, Button } from 'react-bootstrap';

import { PHSpinnerButton } from 'src/ui/common/element/muButton';
import { PHFormText } from 'src/ui/common/element/muFormText';
import { ListRow } from 'src/ui/common/element/muListRow';
import './accountConfig.css';

export const AccountConfig = () => {
  const navigate = useNavigate();

  return <section className='accountMain'>
    <header>
      <h2>계정 설정</h2>
    </header>
    <aside className='accountConfigAside'>
      <ListRow
        itemWidth={[30, 70]}
        style={{margin: 0, padding: '0.5rem 0'}}>
        {/* User profile image / nickname / id / email */}
        <div className='accountConfigProfileImgContainer'>
          <img alt='profile image' src='https://picsum.photos/id/237/200/300' />
          <div className='accountConfigProfileImgChangeTextContainer'>
            프로필 이미지<br/>변경
          </div>
        </div>
        <div className='accountConfigNickEmailContainer'>
          <h6>NICKNAME&nbsp;&nbsp;<small>@ID</small></h6>
          <p>EMAIL</p>
        </div>
      </ListRow>
      <Button variant='link'>
        <div>
          {/* Change password button */}
          비밀번호 변경
        </div>
      </Button>
      <Button variant='link'>
        <div onClick={() => navigate('/account/deactivate')}>
          {/* Deactivate account */}
          계정 비활성화
        </div>
      </Button>
    </aside>
  </section>;
}
