import './sidebar.css';

import ReactDOM from 'react-dom';

import { Nav, OverlayTrigger, Tooltip } from 'react-bootstrap';
import Icon from '@mdi/react';

import { setRoute } from '@src/redux/modules/route';
import { useDispatch, useSelector } from 'react-redux';

const SidebarItem = (props) => <OverlayTrigger
  placement='right'
  overlay={(titleProps) => <Tooltip id='button-tooltip' {...titleProps}>{props.v.title}</Tooltip>} >
  <Nav.Item>
    <Nav.Link
      className='sidebarIconPills'
      eventKey={props.v.route}
      onClick={() => console.log(props.v.route)}
      style={{
        height: 56 + 'px',
        paddingTop: 0.5 + 'rem',
        paddingBottom: 0.5 + 'rem',

        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',

        borderBottom: '1px solid #dee2e6',
        transition: 0.3 + 's',
      }} >
      <Icon path={props.v.icon} size='16pt' />
    </Nav.Link>
  </Nav.Item>
</OverlayTrigger>

const Sidebar = (props) => {
  let location = useLocation();
  const dispatch = useDispatch();
  const { route } = useSelector(state => state.route);
  const goRoute = (newRoute) => { dispatch(setRoute(newRoute)); }
  goRoute(location.pathname);

  return <div class="mu-sidebar-container bg-light">
    <Nav fill variant='pills' activeKey={route}>
      {Object.entries(props.itemList).map(([k, v]) => <SidebarItem v={v} key={v.targetId} />)}
    </Nav>,
  </div>
}

export { Sidebar };
