import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import parser from 'tld-extract';

import { Form, Button } from 'react-bootstrap';

import { PHSpinnerButton } from 'src/ui/common/element/muButton';
import { PHFormText } from 'src/ui/common/element/muFormText';
import './account.css';

const extractDomainFromEmailAddress = (email:string) => {
  console.log(email);
  const address = email.split('@').pop();
  console.log(address);
  console.log(parser)
  console.log(parser(address));
  return parser(address).domain;
}

export const WelcomeToMuDev = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // let enableGoToMailDomainBtn = false;
  // if (location.state && location.state.email) {
  //   enableGoToMailDomainBtn = true;
  //   console.log(location.state.email);
  //   // location.state.email
  // }

  const enableGoToMailDomainBtn = true;
  const demoEmail = 'musoftware@daum.cc';
  console.log(extractDomainFromEmailAddress(demoEmail));


  let welcomeString = '입력하신 주소로 메일을 보냈습니다,\n'
  welcomeString += '메일에 포함된 주소를 눌러 가입을 마무리 해주세요.\n'
  welcomeString += '메일이 전송되는데 30분 정도 걸릴 수 있어요.'

  return <section className='accountMain'>
    <header>
      <h2>계정 만들기</h2>
    </header>
    <aside className='accountAside'>
      <Form>
        <Form.Group className='accountAsideFormGroup' controlId='formBasicEmail'>
          <Form.Label>MUdev.cc에 오신 것을 환영해요!</Form.Label>
          <PHFormText defaultChildren={welcomeString}></PHFormText><br />
        </Form.Group>

        <div className='accountSubmitBtnContainer'>
          <Button
            variant='outline-light'
            onClick={() => navigate('/')}>
            홈으로 가기
          </Button>
          <Button
            variant='info'
            onClick={() => navigate('/account/signin')}>
            로그인하러 가기
          </Button>
        </div>
        {
          enableGoToMailDomainBtn ?? <div className='accountSubBtnContainer'>
            <Button
              style={{ width: '100%' }}
              variant='outline-info'
              onClick={() => navigate('/account/signin')}>
              이메일 주소의 도메인으로 가기
            </Button>
          </div>
        }
      </Form>
    </aside>
  </section>;
}
