import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';

import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';

import Icon from '@mdi/react';
import { mdiAccountCircle } from '@mdi/js';

import './topbar.css';
import TopBarLogo from './logo_hanshin.svg';
import FrostAPI from 'src/network/api';

export const TopBar: React.FC = () => {
  const navigate = useNavigate();
  const accountInfo = useSelector(state => state.accountReducer);

  const goToSignIn = () => navigate('/account/signin');
  // const goToSignOut = () => frostAPI.signOut().then(() => navigate('/'));

  return <div className='muNavBarContainer'>
    <Navbar className='muNavBar'>
      <Navbar.Brand href='/' className='muNavBarLogoContainer'>
        <img src={TopBarLogo} />
      </Navbar.Brand>

      <NavDropdown align='end' title={<Icon path={mdiAccountCircle} />} className='muNavBarDropdown'>
        <NavDropdown.Header className='muNavBarDropdownItem'>{
          accountInfo.isSignedIn ? `안녕하세요, ${accountInfo.nick}님!` : '로그인을 해 주세요!'
        }</NavDropdown.Header>
        <NavDropdown.Divider className='muNavBarDropdownItem' />
        <NavDropdown.Item className='muNavBarDropdownItem' href='#' onClick={goToSignIn}>로그인</NavDropdown.Item>
      </NavDropdown>

    </Navbar>
  </div>;
}
