import { OverlayTrigger, Tooltip } from 'react-bootstrap';
import Icon from '@mdi/react';
import { mdiPlus, mdiTools, mdiAccountCircle } from '@mdi/js';

import './playlistDetailPage.css';
import { ListRow } from "../../common/element/muListRow";
import { Divider } from "../../common/element/divider";
import { PHButton } from "../../common/element/muButton";
import { YoutubeThumbnail } from '../../common/element/util/youtubeThumbnail';

export class PlaylistListener {
    public static readonly res_type = 'playco_playlist_listener';

    uuid: number;
    name: string;
    img_url?: string | null;

    constructor(inObj: any) {
        this.uuid = inObj.uuid;
        this.name = inObj.name;
        this.img_url = inObj.img_url ?? null;
    }
}


export class PlaylistItem {
    public static readonly res_type = 'playco_playlist_item';

    uuid: number;
    index: string;
    name: string;
    data: object;

    added_by_uuid: number;
    added_by_nick: string;

    link: string;
    link_type: string;
    link_id: string;

    // will be used in client only
    current_listener: PlaylistListener[];

    constructor(inObj: any) {
        this.uuid = inObj.uuid;
        this.index = inObj.index;
        this.name = inObj.name;
        this.data = inObj.data;

        this.added_by_uuid = inObj.added_by_uuid;
        this.added_by_nick = inObj.added_by_nick;

        this.link = inObj.link;
        this.link_type = inObj.link_type;
        this.link_id = inObj.link_id;

        this.current_listener = inObj.current_listener || [];
    }
}


export class Playlist {
    public static readonly res_type = 'playco_playlist';

    uuid: number;
    index: number;
    name: string;

    created_by_uuid: number;
    created_by_nick: string;

    blocked_at?: string | null = null;
    why_blocked?: string | null = null;

    public_accessable: boolean;
    public_modifiable: boolean;

    items: PlaylistItem[];

    // will be used in client only
    current_playing_item_index: number;
    current_listener: PlaylistListener[] = [];

    constructor(inObj: any) {
        this.uuid = inObj.uuid;
        this.index = inObj.index;
        this.name = inObj.name;

        if (!inObj.hasOwnProperty('blocked_at')) {
            this.created_by_uuid = inObj.created_by_uuid;
            this.created_by_nick = inObj.created_by_nick;

            this.public_accessable = inObj.public_accessable;
            this.public_modifiable = inObj.public_modifiable;

            this.items = inObj.items;
        } else {
            this.created_by_uuid = 0;
            this.created_by_nick = '';

            this.public_accessable = false;
            this.public_modifiable = false;

            this.items = [];

            this.blocked_at = inObj.blocked_at;
            this.why_blocked = inObj.why_blocked;
        }

        this.current_playing_item_index = inObj.current_playing_item_index ?? 0;
    }

    addItem(url: string) {

    }

    fetchFromServer() {

    }
}

type PlaylistDetailListenerPropsType = {
    uuid: number;
    nick: string;
    imgUrl?: string | null;
}

export const PlaylistDetailListener = (props: PlaylistDetailListenerPropsType) => {
    return <OverlayTrigger
        placement='bottom'
        overlay={(tooltipProps) => <Tooltip {...tooltipProps}>{props.nick}</Tooltip>} >
        <Icon path={mdiAccountCircle} size='16pt' />
    </OverlayTrigger>;
}

export const PlaylistDetailDummyListener = () => {
    return <div style={{ height: '16pt' }} />;
}

type PlaylistDetailItemPropsType = {
    youtubeId: string;
    title: string;
    listeners: PlaylistListener[];
    selected?: boolean;
}

export const PlaylistDetailItem = (props: PlaylistDetailItemPropsType) => {
    let isSelected = props.selected || false;
    return <ListRow
        onClick={isSelected ? null : ()=>alert('1')}
        className='playCoPlaylistDetailListItem'
        itemWidth={[20, 15]}
        style={{
            // This cannot be moved to css,
            // as we have to overwrite the element style of ListRow.
            alignItems: 'center',
            marginLeft: '0',
            marginRight: '0',
            borderRadius: '0.3rem',
            ...(isSelected ? {
                border: '1px solid rgba(255, 255, 255, 1)',
                backgroundColor: 'rgba(0, 0, 0, 0.4)'
            } : {})
        }}>
        <div className='playCoPlaylistDetailListItemThumbnailContainer'><YoutubeThumbnail youtubeId={props.youtubeId} /></div>
        <div className='playCoPlaylistDetailListItemInfoContainer'>
            <div className='playCoPlaylistDetailListItemTitle'>{props.title}</div>
            <div className='playCoPlaylistDetailListItemListenerListContainer'>
                <div className='playCoPlaylistDetailListItemListenerList'>
                    {(props.listeners.length) ? (props.listeners.map((value, index, array) => {
                        return <PlaylistDetailListener uuid={value.uuid} nick={value.name} imgUrl={value.img_url} />
                    })) : <PlaylistDetailDummyListener />}
                </div>

            </div>
        </div>
    </ListRow>
}

type PlaylistDetailPropsType = {
    playlistData: Playlist;
}

export const PlaylistDetail = (props: PlaylistDetailPropsType) => {
    return <aside className='playCoPlaylistDetailList'>
        {/* playlist aside */}
        <header>
            <h5>{props.playlistData.name}</h5>
        </header>
        <Divider style={{ margin: 'unset', marginLeft: '0', marginRight: '0', marginTop: '1.25rem', marginBottom: '1.25rem' }} />
        <input
            title='Add item on playlist'
            placeholder='재생목록에 추가할 URL'
            className='playCoPlaylistDetailAddTextField'></input>
        <div className='playCoPlaylistDetailBtnGroup'>
            <PHButton smaller variant='outline-warning'>
                <Icon path={mdiTools} size='1.1rem' />&nbsp;&nbsp;재생목록 설정
            </PHButton>
            <PHButton smaller variant='outline-info'>
                <Icon path={mdiPlus} size='1.1rem' />&nbsp;&nbsp;재생목록에 추가하기
            </PHButton>
        </div>
        <div>
            {(props.playlistData.items.length)
                ? props.playlistData.items.map((value, index, array) => <PlaylistDetailItem
                    youtubeId={value.link_id}
                    title={value.name}
                    listeners={value.current_listener}
                    selected={index === props.playlistData.current_playing_item_index}
                />)
                : <ListRow style={{ width: '100%', marginRight: '0', marginLeft: '0' }}>재생목록에 아무 것도 없어요...</ListRow>}
        </div>
    </aside>;
}
