import { Dispatch } from 'redux';
import { FrostError } from 'src/common/error';
import FrostAPI from '../../../network/api';

import {
    ACCOUNT_FETCHING,
    SIGNED_IN,
    SIGNED_OUT,
} from './action';
import { AccountInfo } from './model';

export const setForceAccountInfoActionCreator = (accountInfo: AccountInfo) => {
    return {
        type: SIGNED_IN,
        accountInfo: accountInfo,
    }
}

export const setForceSignOutActionCreator = () => {
    return {
        type: SIGNED_OUT,
        accountInfo: new AccountInfo({ isSignedIn: false }),
    }
}

export const signInActionCreatorAsync = (idOrEmail, password) => async dispatch => {
    dispatch({ type: ACCOUNT_FETCHING });
    console.log(dispatch);
    (new FrostAPI()).signIn(idOrEmail, password).then(
        (result: FrostAPI) => dispatch(() => {
            console.log('signin dispatch called'); console.log(result); return ({
                type: SIGNED_IN,
                accountInfo: new AccountInfo({
                    isSignedIn: true,
                    uuid: result.userData.uuid,
                    id: result.userData.id,
                    nick: result.userData.nickname,
                    email: result.userData.email,
                }),
            })
        })).catch(
            (reason: FrostError) => dispatch(() => {
                console.log('signin catch dispatch called'); console.log(reason); return ({
                    type: SIGNED_OUT,
                    accountInfo: new AccountInfo({
                        isSignedIn: false,
                        frostErrorObj: reason,
                    }),
                })
            }),
        );
}

export const signUpActionCreatorAsync = (id, email, nick, password) => async dispatch => {
    dispatch({ type: ACCOUNT_FETCHING });
    (new FrostAPI())
        .signUp(id, email, password, nick)
        .then((result: FrostAPI) => dispatch(() => ({
            type: SIGNED_IN,
            accountInfo: new AccountInfo({
                isSignedIn: true,
                uuid: result.userData.uuid,
                id: result.userData.id,
                nick: result.userData.nickname,
                email: result.userData.email,
            }),
        })))
        .catch((reason: FrostError) => dispatch(() => ({
            type: SIGNED_OUT,
            accountInfo: new AccountInfo({
                isSignedIn: false,
                frostErrorObj: reason,
            }),
        })));
}

export const signOutActionCreatorAsync = () => async dispatch => {
    dispatch({ type: ACCOUNT_FETCHING });
    (new FrostAPI()).signOut().finally(
        () => dispatch(() => ({
            type: SIGNED_OUT,
            accountInfo: new AccountInfo({ isSignedIn: false }),
        }))
    );
}

export const refreshAccessTokenActionCreatorAsync = () => async dispatch => {
    dispatch({ type: ACCOUNT_FETCHING });
    (new FrostAPI()).refreshAuthentications().then(
        (result: FrostAPI) => dispatch(() => ({
            type: SIGNED_IN,
            accountInfo: new AccountInfo({
                isSignedIn: true,
                uuid: result.userData.uuid,
                id: result.userData.id,
                nick: result.userData.nickname,
                email: result.userData.email,
            }),
        })),
        (reason: FrostError) => dispatch(() => ({
            type: SIGNED_OUT,
            accountInfo: new AccountInfo({
                isSignedIn: false,
                frostErrorObj: reason,
            }),
        })),
    );
}
