import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import FrostAPI from 'src/network/api';
import { refreshAccessTokenActionCreatorAsync } from 'src/redux/modules/account/action_creator';

import Spinner from 'react-bootstrap/Spinner';
import { PHButton } from 'src/ui/common/element/muButton';
import './main.css';

import { PlayCoPlaylistList } from './playlistList';
import { PlaylistListener, PlaylistItem, Playlist } from './playco/model';
import { AccountInfo } from 'src/redux/modules/account/model';

const PlayCoRequireSignedIn = () => {
  const navigate = useNavigate();
  return <aside>
    <header>
      <p>이 기능은 로그인이 필요해요,<br />로그인을 해 주세요.</p>
    </header>
    <PHButton
      onClick={() => navigate('/account/signin')}
      variant='success'
      size='medium'>
      로그인
    </PHButton>
  </aside>;
}

export const PlaycoMain = (props) => {
  const tempPlaylistData = [
    new Playlist({
      uuid: 0,
      index: 0,
      name: '테스트',
      created_by_uuid: 0,
      created_by_nick: '',
      public_accessable: false,
      public_modifiable: false,
      current_playing_item_index: 1,
      items: [],
      blocked_at: '2022-04-14',
      why_blocked: '테스트로 잠김',
    }),
    new Playlist({
      uuid: 0,
      index: 0,
      name: '테스트',
      created_by_uuid: 0,
      created_by_nick: '',
      public_accessable: false,
      public_modifiable: false,
      current_playing_item_index: 1,
      items: [
        new PlaylistItem({
          uuid: 0,
          index: 0,
          name: 'q',
          data: {},
          added_by_uuid: 0,
          added_by_nick: '',
          link: 'https://www.youtube.com/watch?v=2Dw7NFm1ZfY',
          link_type: 'youtube',
          link_id: '2Dw7NFm1ZfY',
          current_listener: [
            new PlaylistListener({ uuid: 1, name: '123' }),
            new PlaylistListener({ uuid: 2, name: '123' }),
            new PlaylistListener({ uuid: 3, name: '123' })
          ],
        }),
        new PlaylistItem({
          uuid: 0,
          index: 0,
          name: 'q',
          data: {},
          added_by_uuid: 0,
          added_by_nick: '',
          link: 'https://www.youtube.com/watch?v=2Dw7NFm1ZfY',
          link_type: 'youtube',
          link_id: '2Dw7NFm1ZfY',
          current_listener: [
            new PlaylistListener({ uuid: 4, name: '123' }),
            new PlaylistListener({ uuid: 5, name: '123' }),
            new PlaylistListener({ uuid: 6, name: '123' })
          ],
        }),
      ],
    }),
  ];
  const frostAPI = new FrostAPI();

  const accountInfo: AccountInfo = useSelector(state => state.accountReducer);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [playCoMainState, setPlayCoMainState] = useState({
    isSignedIn: accountInfo?.isSignedIn || false,
    isAccountFetching: accountInfo?.isFetching || false,
  });

  useEffect(() => {
    setPlayCoMainState({
      ...playCoMainState,
      isSignedIn: accountInfo?.isSignedIn || false,
      isAccountFetching: accountInfo?.isFetching || false,
    })
  }, [accountInfo]);

  useEffect(() => dispatch(refreshAccessTokenActionCreatorAsync()), []);

  const needToShowSpinner = playCoMainState.isAccountFetching;
  const playCoBody = needToShowSpinner
    ? <Spinner animation="border" role="status">
      <span className="visually-hidden">Loading...</span>
    </Spinner>
    : !playCoMainState.isSignedIn
      ? <PlayCoRequireSignedIn />
      : <PlayCoPlaylistList playlistList={tempPlaylistData} />;

  return <section className='playCoMain'>
    <header>
      <h2>PlayCO<sup className='playCoTitleHeaderSup'>Alpha</sup></h2>
      <p>플레이리스트를, 멀리서, 다 같이</p>
    </header>
    {playCoBody}
  </section>
}
