import './postToMU.css';

import { useState } from 'react';
import { Card } from 'react-bootstrap';
import { ListRowLongTextType, L, ListRowInputType, ListRowButtonType, ListRow } from '@ui/common/element/muListRow';

const HomePostToMUSection = props => {
  const [demoData, setDemoData] = useState({
    demoStr: '',
    demoNumeric: 0,
    demoChecked: true,
    demoRadio: undefined,
  });
  const onInputChange = (valueName) => (e) => {
    if (e.target.type === 'reset') {
      setDemoData({
        demoStr: '',
        demoNumeric: 0,
        demoChecked: true,
        demoRadio: undefined,
      });
    } if (valueName) {
      let newDemoData = { ...demoData };
      // TODO: e.target.checked도 받을 수 있도록
      if (e.target.type === 'checkbox') {
        newDemoData[valueName] = e.target.checked;
      } else {
        newDemoData[valueName] = e.target.value;
      }
      console.log(newDemoData);
      setDemoData(newDemoData);
    }
  }

  // return <div {...props}>
  //   <h2>Contact to MU!</h2>
  //   <Card>
  //     <ListRow label='성함'>
  //       {ListRowInputType.n({ name: 'postSender', type: 'text', onChange: onInputChange, state: demoData, min: 5, max: 15, step: 2, })}
  //     </ListRow>
  //     <ListRow label='연락처'>
  //       {ListRowInputType.n({ name: 'postEmail', type: 'email', onChange: onInputChange, state: demoData, min: 5, max: 15, step: 2, })}
  //     </ListRow>
  //     <ListRow label='문의 유형'>
  //       {ListRowInputType.n({ name: 'postType', type: 'number', onChange: onInputChange, state: demoData, min: 5, max: 15, step: 2, })}
  //     </ListRow>
  //     <ListRow label='상세 내용'>
  //       {ListRowInputType.n({ name: 'postDetail', type: 'textarea', onChange: onInputChange, state: demoData, min: 5, max: 15, step: 2, })}
  //     </ListRow>
  //   </Card>
  // </div>;
  return <section className='homePostToMUSection'>
  <header>
    <h2>Contact to MU!</h2>
  </header>
  <form encType='multipart/form-data' method='POST'>
    <label htmlFor='form_name'>이름</label>
    <input type='text' id='form_name' name='form_name' size='32' placeholder='성함을 입력해주세요.' />
    <label htmlFor='form_email'>연락처</label>
    <input type='email' id='form_email' name='form_email' size='32' placeholder='youremail@example.com' />
    <label htmlFor='form_select'>문의 유형</label>
    <select id='form_select'>
        <option value='option1'>팀 가입 문의</option>
        <option value='option2'>협력 문의</option>
        <option value='option3'>보안 신고</option>
        <option value='option4'>권리 침해 이의 제기 및 신고</option>
        <option value='option5'>기타 문의, 이의 제기 및 신고</option>
    </select>
    <label htmlFor='form_detail'>상세 내용</label>
    <textarea cols='40' rows='5' id='form_detail'></textarea>
    <button type='submit'>보내기</button>
</form>
</section>
};

export { HomePostToMUSection };
