import { Tooltip } from 'react-bootstrap';
import { mdiHome, mdiBriefcase, mdiDocker } from '@mdi/js';

const SIDEBAR = {
  HOME: {targetId: 'sidebarHomeBtn', route: '/', icon: mdiHome},
  PROJECT: {targetId: 'sidebarProjectBtn', route: '/projects', icon: mdiBriefcase},
  CONTAINER: {targetId: 'sidebarContainerBtn', route: '/containers', icon: mdiDocker},
};

const SIDEBAR_ELEMENT = Object.entries(SIDEBAR).map(([k, v]) => {
  let sidebarItem = <li class="nav-item mu-sidebar-icon-pills">
    <a href="#" class="nav-link border-bottom mu-sidebar-icon-link active" aria-current="page" title
      data-bs-toggle="tooltip" data-bs-placement="right" data-bs-original-title="í™ˆ" id="sidebarHomeBtn">
      <span class="mdi mdi-home mu-sidebar-icon"></span>
    </a>
  </li>
  sidebarItem.addEventListener('click', () => gotoMenu(v.route));
  new bootstrap.Tooltip(sidebarItem);
  return sidebarItem;


  // var sElement = document.getElementById(v.targetId);
  // sElement.addEventListener('click', () => gotoMenu(v.route));
  // new bootstrap.Tooltip(sElement);
  // return sElement;
});

// // Tooltip enabler
// const tooltipTriggerList = [].slice.call(document.querySelectorAll('[data-bs-toggle="tooltip"]'));
// const tooltipList = tooltipTriggerList.map(function (tooltipTriggerEl) {
//   return new bootstrap.Tooltip(tooltipTriggerEl);
// });

const gotoMenu = (suburl) => {
  // Go to route
  window.history.pushState({}, '', suburl);

  // Remove all tooltip
  for (var tooltipElement of document.getElementsByClassName('tooltip'))
    tooltipElement.remove();
};

function setSidebarActive(sidebarId) {
  SIDEBAR_ELEMENT.forEach(e => {
    if (e.id === sidebarId.targetId) {
      if (!e.classList.contains('active'))
        e.classList.add('active');
    } else {
      if (e.classList.contains('active'))
        e.classList.remove('active');
    }
  });
}

export { SIDEBAR, SIDEBAR_ELEMENT, setSidebarActive };
