import React, { useState } from 'react';
import { OverlayTrigger, Tooltip, Form } from 'react-bootstrap';
import ReactPlayer from 'react-player';
import Icon from '@mdi/react';
import { mdiPlus, mdiTools, mdiAccountCircle } from '@mdi/js';

import { ListRow } from '../../common/element/muListRow';
import { Divider } from '../../common/element/divider';
import { PHButton } from '../../common/element/muButton';
import { YoutubeThumbnail } from '../../common/element/util/youtubeThumbnail';

import { PlaylistControlModal } from './element/playlistControlModal';
import {
  PlaylistListener,
  PlaylistItem,
  Playlist,
  PlaylistConfig
} from './playco/model';
import { PlayCoAPI } from './playco/api';
import './playlistDetailPage.css';

export const PlaylistDetailDummyListener: React.FC = () => <div style={{ height: '16pt' }} />;
export const PlaylistDetailListener: React.FC = (props: {
  uuid: number;
  nick: string;
  imgUrl?: string | null;
}) => {
  return <OverlayTrigger
    placement='bottom'
    overlay={(tooltipProps) => <Tooltip {...tooltipProps}>{props.nick}</Tooltip>} >
    <Icon path={mdiAccountCircle} size='16pt' />
  </OverlayTrigger>;
}


export const PlaylistDetailItem: React.FC = (props: {
  youtubeId: string;
  title: string;
  listeners: PlaylistListener[];
  selected?: boolean;
}) => {
  const isSelected = props.selected || false;
  return <ListRow
    onClick={isSelected ? null : () => alert('1')}
    className='playCoPlaylistDetailListItem'
    itemWidth={[20, 15]}
    style={{
      // This cannot be moved to css,
      // as we have to overwrite the element style of ListRow.
      alignItems: 'center',
      marginLeft: '0',
      marginRight: '0',
      borderRadius: '0.3rem',
      ...(isSelected ? {
        border: '1px solid rgba(255, 255, 255, 1)',
        backgroundColor: 'rgba(0, 0, 0, 0.4)'
      } : {})
    }}>
    <div className='playCoPlaylistDetailListItemThumbnailContainer'><YoutubeThumbnail youtubeId={props.youtubeId} /></div>
    <div className='playCoPlaylistDetailListItemInfoContainer'>
      <div className='playCoPlaylistDetailListItemTitle'>{props.title}</div>
      <div className='playCoPlaylistDetailListItemListenerListContainer'>
        <div className='playCoPlaylistDetailListItemListenerList'>
          {(props.listeners.length) ? (props.listeners.map(value => {
            return <PlaylistDetailListener
              key={`playco-playlist-detail-listitem-listener-${value.uuid}`}
              uuid={value.uuid}
              nick={value.name}
              imgUrl={value.img_url} />
          })) : <PlaylistDetailDummyListener />}
        </div>

      </div>
    </div>
  </ListRow>;
};
export const PlaylistDetail: React.FC = (props: {
  setPlaylistModifyModalShowStateFunc: (boolean) => void,
  playlistData: Playlist;
}) => {
  return <aside className='playCoPlaylistDetailList'>
    <header>
      <h5>{props.playlistData.name}</h5>
    </header>
    <Divider style={{ margin: 'unset', marginLeft: '0', marginRight: '0', marginTop: '1.25rem', marginBottom: '1.25rem' }} />
    <input
      title='Add item on playlist'
      placeholder='재생목록에 추가할 URL'
      className='playCoPlaylistDetailAddTextField'></input>
    <div className='playCoPlaylistDetailBtnGroup'>
      <PHButton
        smaller
        variant='outline-warning'
        onClick={() => props.setPlaylistModifyModalShowStateFunc(true)}>
        <Icon path={mdiTools} size='1.1rem' />&nbsp;&nbsp;재생목록 설정
      </PHButton>
      <PHButton smaller variant='outline-info'>
        <Icon path={mdiPlus} size='1.1rem' />&nbsp;&nbsp;재생목록에 추가하기
      </PHButton>
    </div>
    <div>
      {(props.playlistData.items.length)
        ? props.playlistData.items.map((value, index) => <PlaylistDetailItem
          key={`playco-playlist-detail-listitem-${value.uuid}`}
          youtubeId={value.link_id}
          title={value.name}
          listeners={value.current_listener}
          selected={index === props.playlistData.current_playing_item_index} />)
        : <ListRow style={{ width: '100%', marginRight: '0', marginLeft: '0' }}>재생목록에 아무 것도 없어요...</ListRow>}
    </div>
  </aside>;
}


export const PlayCoPlaylistDetail: React.FC = () => {
  const dummyPlaylistData = new Playlist({
    uuid: 0,
    index: 0,
    name: '테스트',
    created_by_uuid: 0,
    created_by_nick: '',
    public_accessable: false,
    public_modifiable: false,
    current_playing_item_index: 1,
    items: [
      new PlaylistItem({
        uuid: 0,
        index: 0,
        name: 'q',
        data: {},
        added_by_uuid: 0,
        added_by_nick: '',
        link: 'https://www.youtube.com/watch?v=2Dw7NFm1ZfY',
        link_type: 'youtube',
        link_id: '2Dw7NFm1ZfY',
        current_listener: [
          new PlaylistListener({ uuid: 0, name: '123' }),
          new PlaylistListener({ uuid: 0, name: '345' }),
          new PlaylistListener({ uuid: 0, name: '2234' })
        ],
      }),
      new PlaylistItem({
        uuid: 0,
        index: 0,
        name: 'q',
        data: {},
        added_by_uuid: 0,
        added_by_nick: '',
        link: 'https://www.youtube.com/watch?v=2Dw7NFm1ZfY',
        link_type: 'youtube',
        link_id: '2Dw7NFm1ZfY',
        current_listener: [
          new PlaylistListener({ uuid: 0, name: '123' }),
          new PlaylistListener({ uuid: 0, name: '123' }),
          new PlaylistListener({ uuid: 0, name: '123' })
        ],
      }),
    ],
  });

  const [playlistDetailState, setPlaylistDetailState] = useState({
    playlistData: dummyPlaylistData,
    showPlaylistModifyModal: false,

    autoplay: false,
    currentPlayUrl: 'https://www.youtube.com/watch?v=AH1tszKHCH4',
  });

  const setPlaylistModifyModalShowState = (newState: boolean) => {
    setPlaylistDetailState({
      ...playlistDetailState,
      showPlaylistModifyModal: newState,
    })
  };
  const playerConfig = {
    youtube: {
      playerVars: {
        autoplay: playlistDetailState.autoplay ? 1 : 0,
        controls: 1,
      }
    }
  }

  return <section className='playCoPlaylistDetailPage'>
    <header>
      <h2>PlayCO</h2>
    </header>
    <div className='playCoPlaylistDetail'>
      <PlaylistControlModal
        modalShowState={playlistDetailState.showPlaylistModifyModal}
        setModalShowState={setPlaylistModifyModalShowState} />
      {/* playlist aside */}
      <PlaylistDetail
        playlistData={playlistDetailState.playlistData}
        setPlaylistModifyModalShowStateFunc={setPlaylistModifyModalShowState} />
      <aside className='playCoPlaylistDetailPlayerAside'>
        {/* player aside */}
        <div className='playCoPlaylistDetailPlayerContainer'>
          <ReactPlayer
            url={playlistDetailState.currentPlayUrl}
            className='playCoPlaylistDetailPlayer'
            width='100%' height='100%'
            config={playerConfig} />
        </div>
        <div className='playCoPlaylistDetailPlayerBtnGroup'>
          <Form.Switch
            label='자동재생'
            checked={playlistDetailState.autoplay}
            onChange={() => setPlaylistDetailState({ ...playlistDetailState, autoplay: !playlistDetailState.autoplay })}
          />
        </div>
      </aside>
    </div>
  </section>;
};
