import Icon from '@mdi/react';
import { mdiHome, mdiBriefcase, mdiDocker } from '@mdi/js';
import { Nav, OverlayTrigger, Tooltip } from 'react-bootstrap';
import ReactDOM from 'react-dom';

import styles from './Sidebar.css';

const SIDEBAR_CONTAINER = document.getElementsByClassName('mu-sidebar-container')[0];
const SIDEBAR_ITEMS = {
  HOME: { targetId: 'sidebarHomeBtn', title: '홈', route: '/', icon: mdiHome },
  PROJECT: { targetId: 'sidebarProjectBtn', title: '프로젝트', route: '/projects', icon: mdiBriefcase },
  CONTAINER: { targetId: 'sidebarContainerBtn', title: '컨테이너', route: '/containers', icon: mdiDocker },
};

const SIDEBAR_ELEMENT = Object.entries(SIDEBAR_ITEMS).map(([k, v]) => {
  return <OverlayTrigger placement="right" overlay={(props) => <Tooltip id='button-tooltip' {...props}>{v.title}</Tooltip>}>
    <Nav.Item>
      <Nav.Link
        onClick={() => gotoMenu(v.route)} eventKey={v.route}
        style={{
          paddingTop: 0.5 + 'rem',
          paddingBottom: 0.5 + 'rem',
          height: 56 + 'px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }} >
        <Icon path={v.icon} size='16pt' />
      </Nav.Link>
    </Nav.Item>
  </OverlayTrigger>
});
const SIDEBAR = <Nav fill variant='pills' defaultActiveKey='/'>{SIDEBAR_ELEMENT}</Nav>;

ReactDOM.render(SIDEBAR, SIDEBAR_CONTAINER);

const gotoMenu = (suburl) => {
  // Go to route
  window.history.pushState({}, '', suburl);

  // Remove all tooltip
  // for (var tooltipElement of document.getElementsByClassName('tooltip'))
  //   tooltipElement.remove();
};

function setSidebarActive(sidebarItem) {

}

export { SIDEBAR_ITEMS, SIDEBAR_ELEMENT, setSidebarActive };
