import Icon from '@mdi/react';
import { mdiHome, mdiBriefcase, mdiDocker } from '@mdi/js';
import { Nav, OverlayTrigger, Tooltip } from 'react-bootstrap';
import ReactDOM from 'react-dom';

import style from './Sidebar.css';

const SIDEBAR = document.getElementsByClassName('mu-sidebar')[0];
const SIDEBAR_ITEMS = {
  HOME: { targetId: 'sidebarHomeBtn', title: '홈', route: '/', icon: mdiHome },
  PROJECT: { targetId: 'sidebarProjectBtn', title: '프로젝트', route: '/projects', icon: mdiBriefcase },
  CONTAINER: { targetId: 'sidebarContainerBtn', title: '컨테이너', route: '/containers', icon: mdiDocker },
};

const SIDEBAR_ELEMENT = Object.entries(SIDEBAR_ITEMS).map(([k, v]) => {
  let sidebarItem = <Nav.Item>
    <Nav.Link>
      <OverlayTrigger

        className={style.sidebarIconPills}
        onClick={() => gotoMenu(v.route)}
        placement="right"
        overlay={(props) => <Tooltip id='button-tooltip' {...props}>{v.title}</Tooltip>}
      >
        <Icon path={v.icon} size="16px" />
      </OverlayTrigger>
    </Nav.Link>
  </Nav.Item>

  return sidebarItem;

  // var sElement = document.getElementById(v.targetId);
  // sElement.addEventListener('click', () => gotoMenu(v.route));
  // new bootstrap.Tooltip(sElement);
  // return sElement;
});

ReactDOM.render(<>{SIDEBAR_ELEMENT}</>, SIDEBAR);

// // Tooltip enabler
