import './playlistDetailPage.css';

import { useState } from 'react';
import ReactPlayer from 'react-player';
import Form from 'react-bootstrap/Form'

import Icon from '@mdi/react';
import { mdiPlus, mdiTools } from '@mdi/js';

import { PHButton } from '@src/ui/common/element/muButton';
import { Divider } from 'src/ui/common/element/divider';

import { Playlist } from './playlistResource';


const PlayCoPlaylistDetail = (props) => {
  const [playerState, setPlayerState] = useState({
    autoplay: false,
  });

  new Playlist({
    uuid:0, index:0, name:'', created_by_uuid: 0, created_by_nick: '', public_accessable: false, public_modifiable: false,
  })

  return <section className='playCoPlaylistDetailPage' {...props}>
    <header>
      <h2>PlayCO</h2>
    </header>
    <div className='playCoPlaylistDetail'>
      <aside className='playCoPlaylistDetailList'>
        {/* playlist aside */}
        <header>
          <h5>{'``INSERT 플레이리스트 이름 HERE``'}</h5>
        </header>
        <Divider style={{ margin: 0, marginTop: '1.25rem', marginBottom: '1.25rem' }} />
        <input className='playCoPlaylistDetailAddTextField'></input>
        <div className='playCoPlaylistDetailBtnGroup'>
          <PHButton smaller variant='outline-warning'>
            <Icon path={mdiTools} size='1.1rem' />&nbsp;&nbsp;재생목록 설정
          </PHButton>
          <PHButton smaller variant='outline-info'>
            <Icon path={mdiPlus} size='1.1rem' />&nbsp;&nbsp;재생목록에 추가하기
          </PHButton>
        </div>
      </aside>
      <aside className='playCoPlaylistDetailPlayerAside'>
        {/* player aside */}
        <div className='playCoPlaylistDetailPlayerContainer'>
          <ReactPlayer
            className='playCoPlaylistDetailPlayer'
            width='100%'
            height='100%'
            config={{
              youtube: {
                playerVars: {
                  autoplay: playerState.autoplay ? 1 : 0,
                }
              },
            }}
          />
        </div>
        <div className='playCoPlaylistDetailPlayerBtnGroup'>
          <Form.Switch
            label='자동재생'
            checked={playerState.autoplay}
            onChange={() => setPlayerState({ ...playerState, autoplay: !playerState.autoplay })}
          />
        </div>
      </aside>
    </div>
  </section>;
};

export { PlayCoPlaylistDetail };
