import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Modal, Button } from "react-bootstrap";
import Form from 'react-bootstrap/Form';
import { PHFormText } from 'src/ui/common/element/muFormText';
import { PHSpinnerButton } from 'src/ui/common/element/muButton';

interface PlaylistControlModalPropTypes {
  modalShowState: boolean;
  setModalShowState: (boolean) => void;
  createMode?: boolean;
}

export const PlaylistControlModal = (props: PlaylistControlModalPropTypes) => {
  const navigate = useNavigate();
  const [playlistControlModalState, setPlaylistControlModalState] = useState({
    isProcessing: false,

    nameTextFieldValue: '',
    nameNotUsableReason: '',
    isPublicAccessable: false,
    isPublicItemAppendable: false,
    isPublicItemDeletable: false,
    playlistCreationFailedReason: '',
  });
  const closeModalFunc = () => {
    if (!playlistControlModalState.isProcessing)
      props.setModalShowState(false);
  };
  const handleInputChange = (valueName) => (e) => {
    let newPlaylistControlModalState = {
      ...playlistControlModalState,
      [valueName]: (e.target.type === 'checkbox') ? e.target.checked : e.target.value,
    };
    if (valueName === 'nameTextFieldValue')
      newPlaylistControlModalState.nameNotUsableReason = '';
    setPlaylistControlModalState(newPlaylistControlModalState);
  }
  const tryPlaylistCreate = () => {
    let isRequestable = true;
    let newPlaylistControlModalState = {
      ...playlistControlModalState,
      isProcessing: true,
    };

    // trim all strings in states
    for (var key in newPlaylistControlModalState)
      if (typeof (newPlaylistControlModalState[key]) === 'string')
        newPlaylistControlModalState[key] = newPlaylistControlModalState[key].trim();

    if (!newPlaylistControlModalState.nameTextFieldValue) {
      newPlaylistControlModalState.nameNotUsableReason = '재생목록 이름을 적어주세요!';
      isRequestable = false;
    }
    setPlaylistControlModalState(newPlaylistControlModalState);

    if (isRequestable) {
      //
    }
    console.log(playlistControlModalState);

    setTimeout(() => {
      newPlaylistControlModalState = {
        ...newPlaylistControlModalState,
        isProcessing: false,
      };
      setPlaylistControlModalState(newPlaylistControlModalState);
    }, 5000);
  }
  const tryPlaylistModify = () => {
    console.log('qwe');
  }

  return <Modal
    show={props.modalShowState}
    onHide={closeModalFunc}
    backdrop='static'
    keyboard={false}
    centered >
    <Modal.Header closeButton>
      <Modal.Title>
        <h5 style={{ margin: 0, }}>
          {(props.createMode) ? '재생목록 만들기' : '재생목록 수정'}
        </h5>
      </Modal.Title>
    </Modal.Header>
    <Modal.Body>
      <Form>
        <Form.Group>
          <Form.Label>재생목록 이름</Form.Label>
          <Form.Control
            type='text'
            placeholder='새로 만드실 재생목록의 이름을 적어주세요'
            disabled={playlistControlModalState.isProcessing}
            value={playlistControlModalState.nameTextFieldValue}
            onChange={handleInputChange('nameTextFieldValue')} />
          <PHFormText>{playlistControlModalState.nameNotUsableReason}</PHFormText>
        </Form.Group>
        <Form.Group>
          <Form.Label>재생목록 설정</Form.Label>
          <Form.Switch
            onChange={handleInputChange('isPublicAccessable')}
            value={playlistControlModalState.isPublicAccessable}
            disabled={playlistControlModalState.isProcessing}
            id='isPublicAccessableSwitch'
            label='다른 사람이 재생목록을 볼 수 있어요.' />
          <Form.Switch
            onChange={handleInputChange('isPublicItemAppendable')}
            value={playlistControlModalState.isPublicItemAppendable}
            disabled={playlistControlModalState.isProcessing}
            id='isPublicItemAppendableSwitch'
            label='다른 사람이 재생목록에 영상을 추가할 수 있어요.' />
          <Form.Switch
            onChange={handleInputChange('isPublicItemDeletable')}
            value={playlistControlModalState.isPublicItemDeletable}
            disabled={playlistControlModalState.isProcessing}
            id='isPublicItemDeletableSwitch'
            label='다른 사람이 재생목록에서 영상을 삭제할 수 있어요.' />
        </Form.Group>
        <PHFormText>{playlistControlModalState.playlistCreationFailedReason}</PHFormText>
      </Form>
    </Modal.Body>
    <Modal.Footer>
      <Button
        variant='secondary'
        onClick={closeModalFunc}
        disabled={playlistControlModalState.isProcessing}>
        취소
      </Button>

      <PHSpinnerButton
        variant='primary'
        size={false}
        style={{ margin: undefined }}
        onClick={(props.createMode) ? tryPlaylistCreate : tryPlaylistModify}
        showSpinner={playlistControlModalState.isProcessing}>
        {(props.createMode) ? '재생목록 만들기' : '재생목록 수정'}
      </PHSpinnerButton>
    </Modal.Footer>
  </Modal>;
}
