import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { isEmail } from 'validator';

import { Form, Modal, Button } from "react-bootstrap";
import { FrostError } from 'src/common/error';

import { PHFormText } from 'src/ui/common/element/muFormText';
import { PHSpinnerButton } from 'src/ui/common/element/muButton';
import { AccountInfo } from 'src/redux/modules/account/model';

const calculateIncludedCharType = (str: string) => {
    const strType: string[] = [];

    for (let i = 0; i < str.length; i++) {
        const currentChar = str.charAt(i);
        if (currentChar >= '0' && currentChar <= '9') {
            strType.push('n'); // number
        } else if (currentChar >= 'a' && currentChar <= 'z') {
            strType.push('l'); // lower
        } else if (currentChar >= 'A' && currentChar <= 'Z') {
            strType.push('u'); // upper
        } else if (currentChar >= ' ' && currentChar <= '~') {
            strType.push('s'); // special char
        } else {
            strType.push('e'); // error or unknown
        }
    }
    return new Set(strType);
};

export const AccountNickIDChangeModal: React.FC = (props: {
    modalShowState: boolean;
    setModalShowState: (boolean) => void;
    initialModalState: AccountInfo;
}) => {
    const InputChecker = {
        id: (str: string) => {
            if (!str) {
                return {
                    success: false,
                    idNotUsableReason: '아이디를 입력해주세요.',
                };
            } else if (str.length < 4) {
                return {
                    success: false,
                    idNotUsableReason: '아이디의 길이가 너무 짧아요, 4자 이상으로 적어주세요.',
                };
            } else if (48 < str.length) {
                return {
                    success: false,
                    idNotUsableReason: '아이디의 길이가 너무 길어요, 48자 이하로 적어주세요.',
                };
            }

            const strType = calculateIncludedCharType(str);
            if (strType.has('e')) {
                return {
                    success: false,
                    idNotUsableReason: '아이디로 사용할 수 없는 글자가 포함되어 있어요, 다시 입력해주세요.',
                };
            }

            return { success: true, idNotUsableReason: null };
        },
        nick: (str: string) => {
            if (!str) {
                return {
                    success: false,
                    nickNotUsableReason: '별칭을 입력해주세요.',
                };
            }

            return {
                success: true,
                nickNotUsableReason: null,
            };
        },
    };





    const navigate = useNavigate();
    const [nickIdChangeModalState, setNickIdChangeModalState] = useState({
        isProcessing: false,

        nickTextFieldValue: props.initialModalState.nick ?? '',
        nickNotUsableReason: '',
        idTextFieldValue: props.initialModalState.id ?? '',
        idNotUsableReason: '',
        nickIdChangeFailedReason: '',
    });
    useEffect(() => setNickIdChangeModalState({
        ...nickIdChangeModalState,
        nickTextFieldValue: props.initialModalState?.nick ?? '',
        nickNotUsableReason: '',
        idTextFieldValue: props.initialModalState?.id ?? '',
        idNotUsableReason: '',
        nickIdChangeFailedReason: '',
    }), [props.initialModalState]);

    const closeModalFunc = () => {
        if (!nickIdChangeModalState.isProcessing)
            props.setModalShowState(false);
    };
    const handleInputChange = (valueName) => (e) => {
        const newModalState = {
            ...nickIdChangeModalState,
            [valueName]: (e.target.type === 'checkbox') ? e.target.checked : e.target.value,
        };
        if (valueName === 'nickTextFieldValue')
            newModalState.nameNotUsableReason = '';
        else if (valueName === 'idTextFieldValue')
            newModalState.idNotUsableReason = '';
        setNickIdChangeModalState(newModalState);
    };
    const whatChanged = () => {
        return {
            nick: nickIdChangeModalState.nickTextFieldValue !== props.initialModalState.nick,
            id: nickIdChangeModalState.idTextFieldValue !== props.initialModalState.id
        };
    };
    const tryAccountInfoChange = () => {
        let isRequestable = true;
        setNickIdChangeModalState((prevState) => ({ ...prevState, isProcessing: true, }));

        const newModalState = {
            ...nickIdChangeModalState,
            isProcessing: true,
        };
        // trim all strings in states
        for (const key in newModalState)
            if (typeof (newModalState[key]) === 'string')
                newModalState[key] = newModalState[key].trim();

        const isChanged = whatChanged();
        if (!isChanged.id && !isChanged.nick) {
            // Stats are not modified, just close it.
            newModalState.isProcessing = false;
            isRequestable = false;
            setNickIdChangeModalState(newModalState);
            closeModalFunc();
            return;
        }

        if (!newModalState.nickTextFieldValue) {
            // nickname input field must be filled.
            newModalState.nickNotUsableReason = '별칭을 적어주세요.';
            newModalState.isProcessing = false;
            isRequestable = false;
        }
        if (!newModalState.idTextFieldValue) {
            // id input field must be filled.
            newModalState.idNotUsableReason = '아이디를 적어주세요.';
            newModalState.isProcessing = false;
            isRequestable = false;
        } else if (isChanged.id) {
            // id input field value is changed.
            const checkResult = InputChecker.id(newModalState.idTextFieldValue);

            if (!checkResult.success) {
                newModalState.idNotUsableReason = checkResult.idNotUsableReason;
                newModalState.isProcessing = false;
                isRequestable = false;
            }
        }
        setNickIdChangeModalState(newModalState);

        if (isRequestable) {
            // DO SERVER THING ON HERE
            setTimeout(() => {
                setNickIdChangeModalState((prevState) => ({ ...prevState, isProcessing: false, }));
            }, 5000);
        }
    };
    const resetToOriginal = () => {
        setNickIdChangeModalState({
            ...nickIdChangeModalState,
            nickTextFieldValue: props.initialModalState.nick ?? '',
            nickNotUsableReason: '',
            idTextFieldValue: props.initialModalState.id ?? '',
            idNotUsableReason: '',
        });
    };
    const getSubmitBtnText = () => {
        const result = whatChanged();
        if (result.id && result.nick) {
            return ['아이디 및 별칭 수정하기', true];
        } else if (result.id) {
            return ['아이디 수정하기', true];
        } else if (result.nick) {
            return ['별칭 수정하기', true];
        } else {
            return ['수정된 정보가 없어요', false];
        }
    };

    const submitBtnStat = getSubmitBtnText();

    return <Modal
        show={props.modalShowState}
        onHide={closeModalFunc}
        backdrop='static'
        centered >
        <Modal.Header closeButton>
            <Modal.Title>
                <h5 style={{ margin: 0, }}>
                    아이디 및 별칭 변경
                </h5>
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form onSubmit={evt => evt.preventDefault()}>
                <Form.Group>
                    <Form.Label>아이디</Form.Label>
                    <Form.Control
                        type='text'
                        placeholder='여기에 새로 사용하실 아이디를 적어주세요.'
                        disabled={nickIdChangeModalState.isProcessing}
                        value={nickIdChangeModalState.idTextFieldValue}
                        onChange={handleInputChange('idTextFieldValue')} />
                    <PHFormText>{nickIdChangeModalState.idNotUsableReason}</PHFormText>
                </Form.Group>

                <Form.Group>
                    <Form.Label>별칭</Form.Label>
                    <Form.Control
                        type='text'
                        placeholder='여기에 새로 사용하실 별칭을 적어주세요.'
                        disabled={nickIdChangeModalState.isProcessing}
                        value={nickIdChangeModalState.nickTextFieldValue}
                        onChange={handleInputChange('nickTextFieldValue')} />
                    <PHFormText>{nickIdChangeModalState.nickNotUsableReason}</PHFormText>
                </Form.Group>
                <PHFormText>{nickIdChangeModalState.nickIdChangeFailedReason}</PHFormText>
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button
                variant='secondary'
                onClick={resetToOriginal}
                disabled={nickIdChangeModalState.isProcessing}>
                되돌리기
            </Button>

            <Button
                variant='secondary'
                onClick={closeModalFunc}
                disabled={nickIdChangeModalState.isProcessing}>
                취소
            </Button>

            <PHSpinnerButton
                variant='primary'
                size={false}
                style={{ margin: undefined }}
                onClick={tryAccountInfoChange}
                disabled={!submitBtnStat[1]}
                showSpinner={nickIdChangeModalState.isProcessing}>
                {submitBtnStat[0]}
            </PHSpinnerButton>
        </Modal.Footer>
    </Modal>;
}
