import './sidebar.css';

import { useNavigate, useLocation } from 'react-router-dom';
import { Nav, OverlayTrigger, Tooltip } from 'react-bootstrap';
import Icon from '@mdi/react';
import { mdiHome, mdiBriefcase, mdiDocker, mdiTools } from '@mdi/js';

const SIDEBAR_ITEMS = {
  HOME: { targetId: 'sidebarHomeBtn', title: '홈', route: '/', icon: mdiHome },
  PROJECT: { targetId: 'sidebarBCaBtn', title: 'B.Ca', route: '/bca', icon: mdiBriefcase },
  CONTAINER: { targetId: 'sidebarPlayCoBtn', title: 'PlayCO', route: '/playco', icon: mdiDocker },
  TOOL: { targetId: 'sidebarToolBtn', title: '도구들', route: '/tool', icon: mdiTools },
};

function getSidebarItemUsingRoute(route) {
  for (const [k, v] of Object.entries(SIDEBAR_ITEMS)) {
    if (v.route === '/') { if (v.route === route) return [k, v]; }
    else { if (route.startsWith(v.route)) return [k, v]; }
  }
  return [null, { route: '/unknown' }];
}

const SidebarItem = (props) => {
  let navigate = useNavigate();

  return <OverlayTrigger
    placement='right'
    overlay={(titleProps) => <Tooltip id='button-tooltip' {...titleProps}>{props.v.title}</Tooltip>} >
    <Nav.Item>
      <Nav.Link
        className='sidebarIconPills'
        eventKey={props.v.route}
        onClick={() => navigate(props.v.route)} >
        <Icon path={props.v.icon} size='16pt' />
      </Nav.Link>
    </Nav.Item>
  </OverlayTrigger>;
}

const Sidebar = () => {
  let location = useLocation();
  let sidebarItem = getSidebarItemUsingRoute(location.pathname);
  let onSidebarPullerClick = (event) => {
    console.log(event);
    document.getElementById('sidebarContainer').style.left = '0';
  }
  console.log(onSidebarPullerClick);

  return <div className='sidebarContainer' id='sidebarContainer'>
    <Nav fill variant='pills' activeKey={sidebarItem[1].route}>
      {Object.entries(SIDEBAR_ITEMS).map(([k, v]) => <SidebarItem v={v} key={v.targetId} />)}
    </Nav>
    <div className='sidebarContainerPuller' onClick={onSidebarPullerClick}>▶</div>
  </div>
}

export { Sidebar };
