import { useState } from 'react';
import ReactDOM from 'react-dom';
import { Nav, OverlayTrigger, Tooltip } from 'react-bootstrap';
import { mdiHome, mdiBriefcase, mdiDocker } from '@mdi/js';
import Icon from '@mdi/react';

import './sidebar.css';
import { useLocation } from 'react-router-dom';

const SidebarItem = (props) => <OverlayTrigger
    placement='right'
    overlay={(titleProps) => <Tooltip id='button-tooltip' {...titleProps}>{props.v.title}</Tooltip>} >
  <Nav.Item>
    <Nav.Link
      className='sidebarIconPills'
      eventKey={props.v.route}
      onClick={() => window.history.pushState({}, '', props.v.route)}
      style={{
        height: 56 + 'px',
        paddingTop: 0.5 + 'rem',
        paddingBottom: 0.5 + 'rem',

        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',

        borderBottom: '1px solid #dee2e6',
        transition: 0.3 + 's',
      }} >
      <Icon path={props.v.icon} size='16pt' />
    </Nav.Link>
  </Nav.Item>
</OverlayTrigger>

const SIDEBAR_ITEMS = {
  HOME: { targetId: 'sidebarHomeBtn', title: '홈', route: '/', icon: mdiHome },
  PROJECT: { targetId: 'sidebarProjectBtn', title: '프로젝트', route: '/projects', icon: mdiBriefcase },
  CONTAINER: { targetId: 'sidebarContainerBtn', title: '컨테이너', route: '/containers', icon: mdiDocker },
};

const SIDEBAR_CONTAINER = document.getElementsByClassName('mu-sidebar-container')[0];
const SIDEBAR_ELEMENT = Object.entries(SIDEBAR_ITEMS).map(([k, v]) => <SidebarItem v={v} key={v.targetId} />);
const SIDEBAR = () => {
  const location = useLocation();
  console.log(location.pathname)

  return <Nav fill activeKey={location.pathname} variant='pills' defaultActiveKey='/'>
    {SIDEBAR_ELEMENT}
  </Nav>
};
ReactDOM.render(SIDEBAR(), SIDEBAR_CONTAINER);

function setSidebarActive(sidebarItem) {
}

export { SIDEBAR_ITEMS, setSidebarActive };
