import { useNavigate } from 'react-router-dom';
import Icon from '@mdi/react';
import { mdiPlus, mdiCheckBold } from '@mdi/js';

import { ListRow } from 'src/ui/common/element/muListRow';
import { PHButton } from "../../common/element/muButton";
import './playlistList.css';

import {
  Playlist,
  PlaylistItem,
  PlaylistListener,
  PlaylistDetail
} from './playlistResource';
import { Divider } from 'src/ui/common/element/divider';


const PlayCoPlaylistList = (props) => {
  let navigate = useNavigate();

  let dummyPlaylistListData = [
    new Playlist({
      uuid: 0,
      index: 0,
      name: '테스트',
      created_by_uuid: 0,
      created_by_nick: '',
      public_accessable: false,
      public_modifiable: false,
      current_playing_item_index: 1,
      items: [
        new PlaylistItem({
          uuid: 0,
          index: 0,
          name: 'q',
          data: {},
          added_by_uuid: 0,
          added_by_nick: '',
          link: 'https://www.youtube.com/watch?v=2Dw7NFm1ZfY',
          link_type: 'youtube',
          link_id: '2Dw7NFm1ZfY',
          current_listener: [
            new PlaylistListener({ uuid: 1, name: '123' }),
            new PlaylistListener({ uuid: 2, name: '123' }),
            new PlaylistListener({ uuid: 3, name: '123' })
          ],
        }),
        new PlaylistItem({
          uuid: 0,
          index: 0,
          name: 'q',
          data: {},
          added_by_uuid: 0,
          added_by_nick: '',
          link: 'https://www.youtube.com/watch?v=2Dw7NFm1ZfY',
          link_type: 'youtube',
          link_id: '2Dw7NFm1ZfY',
          current_listener: [
            new PlaylistListener({ uuid: 4, name: '123' }),
            new PlaylistListener({ uuid: 5, name: '123' }),
            new PlaylistListener({ uuid: 6, name: '123' })
          ],
        }),
      ],
    }),
  ]

  return <section className='playCoListMain' {...props}>
    <header>
      <h2>PlayCO</h2>
    </header>
    <aside className='playCoListAside'>
      <div>
        <input
          title='ID of playlist to enter'
          placeholder='참여할 플레이리스트 ID'
          className='playCoIdToEnterTextField'></input>
        <PHButton variant='success' width='100%'>
          <Icon path={mdiCheckBold} size='1.1rem' />&nbsp;&nbsp;재생목록에 참가하기
        </PHButton>
        <Divider style={{ marginLeft: 0, marginRight: 0, marginTop: '1rem', marginBottom: '1rem' }} />

        <header>
          <h5>내 재생목록</h5>
        </header>
        <div className='playCoAsideSectionHeaderButtonContainer'>
          <PHButton smaller variant='outline-info'>
            <Icon path={mdiPlus} size='1.1rem' />&nbsp;&nbsp;새 재생목록 만들기
          </PHButton>
        </div>
        {
          dummyPlaylistListData.map((value, index, array) => {
            return <ListRow onClick={() => navigate('/playco/detail')}>
              <div className='playCoListItemTextContainer'>
                {value.name}
                <div className='playCoListAdditionalInfoTextContainer'>
                  <div>{value.items.length}</div>
                  <div>{value.listener.length}</div>
                </div>
              </div>
            </ListRow>
          })
        }
      </div>
    </aside>
  </section>;
};

export { PlayCoPlaylistList };
