import { io, Manager } from 'socket.io-client';
import { FrostError } from 'src/common/error';
import FrostAPI from 'src/network/api';

interface PlayCoSocketIOResponse {
    code: number;
    subCode: string;
    success: boolean;
    message: string;
    data: Record<string, unknown> | undefined;
}

// Generate random safe string, borrowed from FrostAPI implementation
const generateRandomSecureToken = (bytes: number) => {
    const randArray = new Uint32Array(bytes);
    window.crypto.getRandomValues(randArray);
    return buf2hex(randArray);
}

// From https://stackoverflow.com/a/40031979
// Buffer to hex, also borrowed from FrostAPI implementation
const buf2hex = (buffer: ArrayBufferLike) => {
    return [...new Uint8Array(buffer)]
        .map(x => x.toString(16).padStart(2, '0'))
        .join('');
}

let playCoSocketIOHandlerInstance: PlayCoSocketIOHandler;
export class PlayCoSocketIOHandler {
    private socketIoMgr: io.Manager;
    private socketIo: io;

    private sioToken: string;
    private sioTokenExpiresAt: Date;
    private csrfToken: string;
    private sessionId: string;


    private socketIoEventHandler: Record<string, () => void> = {
        connect: () => {
            this.sessionId = this.socketIo.id;

            // Get SIO Token and register session
            this.refreshSIOToken(true);
        },
        disconnect: (reason) => {
            this.sessionId = '';
            this.sioToken = '';
            this.sioTokenExpiresAt = new Date();
            this.csrfToken = generateRandomSecureToken(32);

        },
        playlistUpdate: () => {
            //
        },
        REQUEST_RESPONSE_0: (data) => {
            console.log('Unknown response received! Received data >>> ----------');
            console.log(data);
            console.log('-------------------------------------------------------');
        },
    };

    constructor() {
        if (playCoSocketIOHandlerInstance) return playCoSocketIOHandlerInstance;

        this.socketIoMgr = new Manager('https://hyperion.mudev.cc', {
            reconnectionDelayMax: 5000,
            path: '/api/dev/ws',
            transports: ['websocket',],
            upgrade: false,

            forceNew: false,
            autoConnect: false,

            closeOnBeforeunload: false,
        });

        this.socketIo = this.socketIoMgr.socket('/playco_ws');
        for (const [key, value] of Object.entries(this.socketIoEventHandler)) {
            this.socketIo.on(key, value);
        }

        playCoSocketIOHandlerInstance = this;
    }

    private emitEvent(
        event: string,
        data: Record<string, unknown> | undefined = undefined,
        onResponse: (response: PlayCoSocketIOResponse) => void | undefined = undefined) {

        const requestId = generateRandomSecureToken(16);
        if (data)
            data['requestId'] = requestId;

        if (onResponse)
            this.socketIo.once(`REQUEST_RESPONSE_${requestId}`, onResponse);

        this.socketIo.emit(event, data);
    }

    private refreshSIOToken(isOnConnect = false) {
        // Check if Socket.IO is connected, and every statement is fulfilled.
        if (!this.socketIo.connected || !this.sessionId) {
            this.disconnect();
            return Promise.reject(new FrostError(
                '재생목록의 실시간 정보를 받아오기 위한 연결에 실패했어요.',
                `this.socketIo.connected = ${this.socketIo.connected}, !this.sessionId = ${this.sessionId}`,
                -1, false
            ));
        }

        // We need to get a new SIO Token by request.
        this.csrfToken = generateRandomSecureToken(32);
        return (new FrostAPI())
            .post('playco/socketio/auth', { 'sid': this.sessionId, 'sio_csrf_token': this.csrfToken, }, true)
            .then(
                (result) => {
                    // Get SIO token from response
                    const sioTokenData: { 'exp': string, 'token': string } = result.data.sio_token;
                    this.sioToken = sioTokenData;
                    this.sioTokenExpiresAt = new Date(sioTokenData.exp);

                    // Register session if this function is called on SocketIO onConnect event.
                    if (isOnConnect) {
                        this.emitEvent(
                            'playco_connect',
                            {
                                sio_token: this.sioToken,
                                sio_csrf_token: this.csrfToken,
                            },
                            (response: PlayCoSocketIOResponse) => {
                                if (response.success) {
                                    // Request success
                                } else {
                                    // Request failed = Session creation failed
                                    throw new FrostError(
                                        response.message, `sio:playco_connect=>response.success=false, ${response.code}`
                                    )
                                }
                            }
                        );
                    }
                },
                (reason: FrostError) => {
                    console.log(reason);
                    console.log(reason.debugMessage);

                    // Failed to get SIO token, disconnect from SocketIO.
                    this.disconnect();

                    // Continue onRejected.
                    reason.message = '재생목록의 실시간 정보를 받아오기 위한 연결을 인증하는데 실패했어요.';
                    throw reason;
                },
            );
    }

    connect() {
        if (!this.socketIo.connected) // Connect SocketIO if it's not connected.
            // This will fire this.socketIoEventHandler.connect if the connection is successfully established.
            this.socketIo.connect();
    }

    disconnect() {
        if (this.socketIo.connected)
            this.socketIo.disconnect();
    }

    enterPlaylist(playlistId: number, hook: (unknown) => void) {
        if (!this.socketIo.connected) {
            return Promise.reject(new FrostError(
                '재생목록의 실시간 정보를 받아오기 위한 연결이 끊어졌어요,\n키보드에서 \'F5\' 버튼을 눌러서 새로고침을 해 주세요.',
                'enterPlaylist->this.socketIo.connected == false', -1, false));
        }

        const reqData = {
            eventName: 'playlist_enter',
            data: {
                playlist_id: playlistId,
                sio_token: this.sioToken,
                sio_csrf_token: this.csrfToken,
            },
            onResponse: (response: PlayCoSocketIOResponse) => {
                if (response.success) {
                    // Request success
                } else {
                    // Request failed
                }
            }
        };
    }

    leavePlaylist(playlistId: number) {
        if (!this.socketIo.connected) {
            return Promise.reject(new FrostError(
                '재생목록의 실시간 정보를 받아오기 위한 연결이 끊어졌어요,\n키보드에서 \'F5\' 버튼을 눌러서 새로고침을 해 주세요.',
                'leavePlaylist->this.socketIo.connected == false', -1, false));
        }

        const reqData = {
            eventName: 'playlist_leave',
            data: {
                playlist_id: playlistId,
                sio_token: this.sioToken,
                sio_csrf_token: this.csrfToken,
            },
            onResponse: (response: PlayCoSocketIOResponse) => {
                if (response.success) {
                    // Request success
                } else {
                    // Request failed
                }
            }
        };
    }

    setSessionCurrentPlaying(playlistId: number, currentPlaying: number) {
        if (!this.socketIo.connected) {
            return Promise.reject(new FrostError(
                '재생목록의 실시간 정보를 받아오기 위한 연결이 끊어졌어요,\n키보드에서 \'F5\' 버튼을 눌러서 새로고침을 해 주세요.',
                'setSessionCurrentPlaying->this.socketIo.connected == false', -1, false));
        }

        const reqData = {
            eventName: 'playlist_set_status',
            data: {
                sio_token: this.sioToken,
                sio_csrf_token: this.csrfToken,
                playlist_id: playlistId,
                currently_playing: currentPlaying,
            },
            onResponse: undefined,
        };
    }
}
