import React from 'react';
import PropTypes from 'prop-types';
import Spinner from 'react-bootstrap/Spinner';
import { Button } from "react-bootstrap";

import './muButton.css'

const REACT_ELEMENT_TYPE = (typeof Symbol === 'function' && Symbol.for && Symbol.for('react.element')) || 0xeac7;

interface PHButtonPropTypes {
  variant?: string;
  disabled?: boolean;
  type?: string;
  size?: string;
  smaller?: boolean;
}

interface PHSpinnerButtonPropTypes extends PHButtonPropTypes {
  showSpinner: boolean;
  spinner?: React.ComponentType;
  setGrayWhenDisabled?: boolean;
}

export const PHButton = (props: PHButtonPropTypes & PropTypes) => {
  const attrCollection = {
    disabled: props.disabled || false,
    onClick: props.onClick,
    size: (props.size === 'extra-sm' ? 'sm' : props.size) ?? 'sm',
    style: (props.smaller || props.size === 'extra-sm')
      ? {
        margin: 0.25 + 'rem',
        width: props.width,
        height: props.height,
        padding: '0rem 0.25rem',
        fontSize: 0.75 + 'rem',
        ...(props.style || {}),
      }
      : {
        margin: 0.25 + 'rem',
        width: props.width,
        height: props.height,
        ...(props.style || {}),
      },
    type: props.type || 'button',
    variant: props.variant || 'outline-primary',
  }

  return <Button {...attrCollection}>
    {props.children}
  </Button>
};

