import './sidebar.css';

import ReactDOM from 'react-dom';

import { Nav, OverlayTrigger, Tooltip } from 'react-bootstrap';
import Icon from '@mdi/react';

import { setRoute } from '@src/redux/modules/route';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, useLocation } from 'react-router-dom';
import { mdiHome, mdiBriefcase, mdiDocker } from '@mdi/js';


const SIDEBAR_ITEMS = {
  HOME: { targetId: 'sidebarHomeBtn', title: '홈', route: '/', icon: mdiHome },
  PROJECT: { targetId: 'sidebarProjectBtn', title: '프로젝트', route: '/projects', icon: mdiBriefcase },
  CONTAINER: { targetId: 'sidebarContainerBtn', title: '컨테이너', route: '/containers', icon: mdiDocker },
};

function getSidebarItemUsingRoute(route) {
  for (const [k, v] of Object.entries(SIDEBAR_ITEMS)) {
    console.log(v.route, route, v.route === route, route.startsWith(v.route));
    if (v.route === route || route.startsWith(v.route))
      return [k, v];
  }
  return [null, null];
}

const SidebarItem = (props) => {
  // const dispatch = useDispatch();
  // const goRoute = (newRoute) => { dispatch(setRoute(newRoute)); }
  let navigate = useNavigate();

  return <OverlayTrigger
    placement='right'
    overlay={(titleProps) => <Tooltip id='button-tooltip' {...titleProps}>{props.v.title}</Tooltip>} >
    <Nav.Item>
      <Nav.Link
        className='sidebarIconPills'
        eventKey={props.v.route}
        onClick={() => navigate(props.v.route)}
        style={{
          height: 56 + 'px',
          paddingTop: 0.5 + 'rem',
          paddingBottom: 0.5 + 'rem',

          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',

          borderBottom: '1px solid #dee2e6',
          transition: 0.3 + 's',
        }} >
        <Icon path={props.v.icon} size='16pt' />
      </Nav.Link>
    </Nav.Item>
  </OverlayTrigger>;
}

const Sidebar = (props) => {
  // const { route } = useSelector(state => state.route);
  let location = useLocation();
  let sidebarItem = getSidebarItemUsingRoute(location.pathname)
  console.log(sidebarItem);
  console.log(location.pathname);

  return <div class="mu-sidebar-container bg-light">
    <Nav fill variant='pills' activeKey={sidebarItem.route}>
      {Object.entries(props.itemList).map(([k, v]) => <SidebarItem v={v} key={v.targetId} />)}
    </Nav>
  </div>
}

export { Sidebar, SIDEBAR_ITEMS };
