import React from 'react';
import PropTypes from 'prop-types';
import Spinner from 'react-bootstrap/Spinner';
import { Button } from "react-bootstrap";

import './muButton.css'

const REACT_ELEMENT_TYPE = (typeof Symbol === 'function' && Symbol.for && Symbol.for('react.element')) || 0xeac7;

interface PHButtonPropTypes {
  variant?: string;
  disabled?: boolean;
  type?: string;
  size?: string;
  smaller?: boolean;
}

interface PHSpinnerButtonPropTypes extends PHButtonPropTypes {
  showSpinner: boolean;
  spinner?: React.ComponentType;
  setGrayWhenDisabled?: boolean;
}

export const PHButton = (props: PHButtonPropTypes & PropTypes) => {
  const resultVariant = props.variant || 'outline-primary';
  const buttonDisabled = props.disabled || false;
  const buttonType = props.type || 'button'
  const buttonSize = (props.size === 'extra-sm' ? 'sm' : props.size) ?? 'sm'
  const buttonStyle = (props.smaller || props.size === 'extra-sm')
    ? {
      margin: 0.25 + 'rem',
      width: props.width,
      height: props.height,
      padding: '0rem 0.25rem',
      fontSize: 0.75 + 'rem',
      ...(props.style || {}),
    }
    : {
      margin: 0.25 + 'rem',
      width: props.width,
      height: props.height,
      ...(props.style || {}),
    };

  const attrCollection = {
    disabled: buttonDisabled,
    onClick: props.onClick,
    size: buttonSize,
    style: buttonStyle,
    type: buttonType,
    variant: resultVariant,
  }

  return <Button {...attrCollection}>
    {props.children}
  </Button>
};

export const PHSpinnerButton: React.FC<PHSpinnerButtonPropTypes> = (inProps: PHSpinnerButtonPropTypes) => {
  const props: PHSpinnerButtonPropTypes = Object.assign({}, inProps, {});

  // When props.showSpinner is true, we disable button and show spinner
  const shouldSpinnerShow = props.showSpinner || false;
  delete (props.showSpinner);

  let spinnerElement = props.spinner || <Spinner animation="border" role="status">
    {/* visually-hidden is Bootstrap's. */}
    <span className="visually-hidden">Loading...</span>
  </Spinner>;
  delete (props.spinner);

  let btnChildElement = props.children || '\u00A0';
  if (React.Children.count(btnChildElement) > 1)
    throw Error('PHSpinnerButton accepts only one element for the children.');
  delete (props.children);


  // If btnChild is not an DOMElement or reactElement, then wrap it with plain div.
  if (!(typeof (btnChildElement) == 'object' && (btnChildElement.$$typeof === REACT_ELEMENT_TYPE)))
    btnChildElement = <div>{btnChildElement}</div>;

  if (shouldSpinnerShow) {
    props.disabled = true;
    if (props.setGrayWhenDisabled) {
      if ((props.variant || '').startsWith('outline-'))
        props.variant = 'outline-secondary';
      else
        props.variant = 'secondary';
    }
  }

  btnChildElement = React.cloneElement(btnChildElement, {
    style: {
      width: 'fit-content',
      height: 'fit-content',
      // visibility: shouldSpinnerShow ? 'hidden' : 'visible',
      opacity: shouldSpinnerShow ? '50%' : '100%',
    }
  });
  spinnerElement = React.cloneElement(spinnerElement, {
    style: {
      visibility: shouldSpinnerShow ? 'visible' : 'hidden',
    }
  });

  return <PHButton {...props}>
    <div className='PHSpinnerButtonChildrenContainer'>
      {btnChildElement}
      <div className='PHSpinnerButtonSpinnerContainer'>
        {spinnerElement}
      </div>
    </div>
  </PHButton>
}
