export const createInitialAccountState = () => {
  return {
    accountInfo: {
      id: '', nick: ''
    }
  }
}

export const SET_ACCOUNT_INFO = 'account/SET_ACCOUNT_INFO';
export const setAccountInfo = (accountInfo) => {
  return {
    type: SET_ACCOUNT_INFO,
    accountInfo: accountInfo,
  }
}

export default function accountReducer(state = createInitialAccountState(), action) {
  switch (action.type) {
    case SET_ACCOUNT_INFO: {
      return {
        ...state,
        accountInfo: action.accountInfo,
      }
    }
    default:
      return state;
  }
};
