import { AccountInfo } from "./model";

const initialAccountState = new AccountInfo({});

export default function accountReducer(state: AccountInfo = initialAccountState, action?: string) {
  if (action)
    switch (action.type) {
      case SET_ACCOUNT_INFO_FORCE:
        return {
          ...state,
          accountInfo: action.accountInfo,
        }

      default:
        break;
    }
  return state;
}
