        link: 'https://www.youtube.com/watch?v=2Dw7NFm1ZfY',
        link_type: 'youtube',
        link_id: '2Dw7NFm1ZfY',
        current_listener: [
          new PlaylistListener({ uuid: 0, name: '123' }),
          new PlaylistListener({ uuid: 1, name: '345' }),
          new PlaylistListener({ uuid: 2, name: '2234' })
        ],
      }),
