        uuid: 1,
        index: 1,
        name: 'w',
        data: {},
        added_by_uuid: 0,
        added_by_nick: '',
        link: 'https://www.youtube.com/watch?v=AH1tszKHCH4',
        link_type: 'youtube',
        link_id: 'AH1tszKHCH4',
        current_listener: [
          new PlaylistListener({ uuid: 3, name: '123' }),
          new PlaylistListener({ uuid: 4, name: '123' }),
          new PlaylistListener({ uuid: 5, name: '123' })
        ],
      }),
    ],
  });

  const [playlistDetailState, setPlaylistDetailState] = useState({
    playlistData: dummyPlaylistData,
    showPlaylistModifyModal: false,

    autoplay: false,
    loopMode: 0, // 0: loop disabled | 1: loop one item | 2: loop all items in playlist
    currentPlayingIndex: 0,
    currentPlayingUrl: dummyPlaylistData.items[0].link,
  });

  const setPlaylistLoopModeState = () => {
    setPlaylistDetailState({
      ...playlistDetailState,
      loopMode: (playlistDetailState.loopMode >= 2) ? 0 : playlistDetailState.loopMode + 1,
    });
  }
  const setPlaylistModifyModalShowState = (newState: boolean) => {
    setPlaylistDetailState({
      ...playlistDetailState,
      showPlaylistModifyModal: newState,
    });
    // TODO: Check if modal is closing, and if it's closing, then refresh the playlist data.
  };
  const setPlaylistCurrentPlayingState = (newIndex: number) => {
    setPlaylistDetailState({
      ...playlistDetailState,
      currentPlayingIndex: newIndex,
      currentPlayingUrl: playlistDetailState.playlistData.items[newIndex].link,
    });
  }
  const setPlaylistCurrentPlayingStateToNext = () => {
    if (playlistDetailState.currentPlayingIndex + 1 >= playlistDetailState.playlistData.items.length) {
      if (playlistDetailState.loopMode === 2)
        setPlaylistCurrentPlayingState(0);
      return;
    }
    setPlaylistCurrentPlayingState(playlistDetailState.currentPlayingIndex + 1);
  }

  return <section className='playCoPlaylistDetailPage'>
    <header>
      <h2>PlayCO</h2>
    </header>
    <div className='playCoPlaylistDetail'>
      <PlaylistControlModal
        initialModalState={playlistDetailState.playlistData.asConfig}
        modalShowState={playlistDetailState.showPlaylistModifyModal}
        setModalShowState={setPlaylistModifyModalShowState} />
      <PlaylistDetail
        playlistData={playlistDetailState.playlistData}
        currentPlayingIndex={playlistDetailState.currentPlayingIndex}
        setPlaylistModifyModalShowStateFunc={setPlaylistModifyModalShowState}
        setPlaylistCurrentPlayingStateFunc={setPlaylistCurrentPlayingState} />
      <PlaylistDetailPlayer
        targetUrl={playlistDetailState.currentPlayingUrl}
        loopMode={playlistDetailState.loopMode}
        setLoopModeStateFunc={setPlaylistLoopModeState}
        autoplay={playlistDetailState.autoplay}
        setAutoplayStateFunc={(newState) => setPlaylistDetailState({ ...playlistDetailState, autoplay: newState })}
        onPlayerEndedFunc={setPlaylistCurrentPlayingStateToNext} />
    </div>
  </section>;
};
