import './sidebar.css';

import ReactDOM from 'react-dom';

import { Nav, OverlayTrigger, Tooltip } from 'react-bootstrap';
import Icon from '@mdi/react';

import store from '@src/redux/store';
import rootReducer from '@src/redux/modules';
import { setRoute } from '@src/redux/modules/route';
import { useDispatch, useSelector } from 'react-redux';

const initializeSidebar = (itemList) => {
  const dispatch = useDispatch();
  const { route } = useSelector(state => state.route);
  const goRoute = (newRoute) => { dispatch(setRoute(newRoute)); }

  const SidebarItem = (props) => <OverlayTrigger
    placement='right'
    overlay={(titleProps) => <Tooltip id='button-tooltip' {...titleProps}>{props.v.title}</Tooltip>} >
    <Nav.Item>
      <Nav.Link
        className='sidebarIconPills'
        eventKey={props.v.route}
        onClick={() => goRoute(props.v.route)}
        style={{
          height: 56 + 'px',
          paddingTop: 0.5 + 'rem',
          paddingBottom: 0.5 + 'rem',

          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',

          borderBottom: '1px solid #dee2e6',
          transition: 0.3 + 's',
        }} >
        <Icon path={props.v.icon} size='16pt' />
      </Nav.Link>
    </Nav.Item>
  </OverlayTrigger>

  const SIDEBAR_CONTAINER = document.getElementsByClassName('mu-sidebar-container')[0];
  const SIDEBAR_ELEMENT = Object.entries(itemList).map(([k, v]) => <SidebarItem v={v} key={v.targetId} />);
  ReactDOM.render(
    <Nav fill variant='pills' activeKey={route}>{SIDEBAR_ELEMENT}</Nav>,
    SIDEBAR_CONTAINER
  );
}

export { initializeSidebar };
