import {
  ACCOUNT_FETCHING,
  SIGNED_IN,
  SIGNED_OUT,
  AccountReducerType,
} from './action';
import {
  signInActionCreatorAsync,
  signUpActionCreatorAsync,
  signOutActionCreatorAsync,
  refreshAccessTokenActionCreatorAsync,
  setAccountInfoForceActionCreator, // TODO: DEBUG PURPOSE, REMOVE THIS ON RELEASE!
} from './action_creator';
import { AccountInfo } from './model';

export default function accountReducer (state = null, action): AccountReducerType {
  switch (action.type) {
    case ACCOUNT_FETCHING:
      return {
        ...state,
        accountInfo: state,
      }

    case SIGNED_IN:
      break;

    default:
      break;
  }
  return state;
}
