import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

import { Oval } from 'react-loader-spinner';

import FrostAPI from 'src/network/api';
import { FrostError } from 'src/common/error';
import { PHButton } from '@src/ui/common/element/muButton';
import './main.css';

import { PlayCoPlaylistList } from './playlistList';
import {
  Playlist,
  PlaylistItem,
  PlaylistListener,
  PlaylistDetail
} from './playlistResource';


const PlayCoRequireSignedIn = (props) => {
  let navigate = useNavigate();

  return <aside>
    <header>
      <h4>
    이 기능은 로그인이 필요해요.

      </h4>
    </header>
    <PHButton onClick={() => navigate('/account/signin')} variant='success' size='medium'>로그인</PHButton>
  </aside>;
}


class PlaycoMain extends React.Component {
  frostAPI = new FrostAPI();

  constructor(props) {
    super(props);

    this.state = {
      isSignedIn: null, // Three state boolean
      playlistData: [
        new Playlist({
          uuid: 0,
          index: 0,
          name: '테스트',
          created_by_uuid: 0,
          created_by_nick: '',
          public_accessable: false,
          public_modifiable: false,
          current_playing_item_index: 1,
          items: []
        }),
        new Playlist({
          uuid: 0,
          index: 0,
          name: '테스트',
          created_by_uuid: 0,
          created_by_nick: '',
          public_accessable: false,
          public_modifiable: false,
          current_playing_item_index: 1,
          items: [
            new PlaylistItem({
              uuid: 0,
              index: 0,
              name: 'q',
              data: {},
              added_by_uuid: 0,
              added_by_nick: '',
              link: 'https://www.youtube.com/watch?v=2Dw7NFm1ZfY',
              link_type: 'youtube',
              link_id: '2Dw7NFm1ZfY',
              current_listener: [
                new PlaylistListener({ uuid: 1, name: '123' }),
                new PlaylistListener({ uuid: 2, name: '123' }),
                new PlaylistListener({ uuid: 3, name: '123' })
              ],
            }),
            new PlaylistItem({
              uuid: 0,
              index: 0,
              name: 'q',
              data: {},
              added_by_uuid: 0,
              added_by_nick: '',
              link: 'https://www.youtube.com/watch?v=2Dw7NFm1ZfY',
              link_type: 'youtube',
              link_id: '2Dw7NFm1ZfY',
              current_listener: [
                new PlaylistListener({ uuid: 4, name: '123' }),
                new PlaylistListener({ uuid: 5, name: '123' }),
                new PlaylistListener({ uuid: 6, name: '123' })
              ],
            }),
          ],
        }),
      ]
    };
  }

  componentDidMount() {
    this.frostAPI.isSignedIn(true).then((signedInResult) => this.setState({ isSignedIn: signedInResult }));
  }

  render() {
    const { isSignedIn, playlistData } = this.state;

    // if isSignedIn is null, then frostAPI is refreshing the token.
    // if isSignedIn is false, then we need to sign-in.
    // else (if isSignedIn is true), then we can show the list of playlists.
    let playCoBody = isSignedIn
      ? <PlayCoPlaylistList playlistList={this.state.playlistData} />
      : isSignedIn === false
        ? <PlayCoRequireSignedIn />
        : <Oval />;

    return <section className='playCoMain'>
      <header>
        <h2>PlayCO</h2>
        <p>플레이리스트를, 멀리서, 그러나 같이</p>
      </header>
      {playCoBody}
    </section>
  }
};

export { PlaycoMain };
