import {
  ACCOUNT_FETCHING,
  SIGNED_IN,
  SIGNED_OUT,
} from './action';
import {
  signInActionCreatorAsync,
  signUpActionCreatorAsync,
  signOutActionCreatorAsync,
  refreshAccessTokenActionCreatorAsync,
  setAccountInfoForceActionCreator, // TODO: DEBUG PURPOSE, REMOVE THIS ON RELEASE!
} from './action_creator';
import { AccountReducerType, AccountInfo } from './model';

const initialAccountState = new AccountInfo({ isSignedIn: false });

export const accountReducer: AccountReducerType = (state = initialAccountState, action) => {
  switch (action.type) {
    case ACCOUNT_FETCHING:
      return new AccountInfo({ isFetching: true });
    case SIGNED_IN:
      return action.accountInfo;
    case SIGNED_OUT:
      return new AccountInfo({ isSignedIn: false });
    default:
      break;
  }
  return state;
}
